Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 49, Ltrim(RTrim('Payroll/Calculation')), Ltrim(RTrim('محاسبات حقوق')), Ltrim(RTrim('چگونه ميتوان در محاسبه  پرداخت عيدي  تعداد روز هاي محاسبه را دستي وارد نمود؟ ')), Ltrim(RTrim('جهت محاسبه  عيدي  با تعداد روز دستي مي بايست به ترتيب مراحل ذيل عمل نمود:   1- در منوي عمودي وارد سيستم  حقوق و دستمزد  شده و از قسمت عمليات گزينه  محاسبات  را انتخاب كرده و در صفحه باز شده از بالاي صفحه نوع محاسبات را  پرداخت عيدي  انتخاب نمائيد.   2- از بالاي صفحه اطلاعات (سال/ماه) را درج كرده سپس گزينه  به روز رساني ليست  را انتخاب نمائيد.   3- در اين قسمت ميتوان با استفاده از گزينه انتخاب چندگانه، كارمند مورد محاسبه را انتخاب كرده و در نهايت با استفاده از كليد ميانبر F3 يا از بالاي صفحه گزينه محاسبه را انتخاب نمود.   4- در كادر باز شده گزينه  تعداد روز معين  را انتخاب كرده و پس از وارد كردن تعداد روز معين گزينه  به روز رساني روزهاي عيدي  را انتخاب كرده و در نهايت گزينه  ادامه را انتخاب نمائيد. ')), 0, 0, 1757 )
