Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 242, Ltrim(RTrim('Payroll/Branches')), Ltrim(RTrim('شعب بيمه و ماليات')), Ltrim(RTrim('روش تعديل ماهانه چيست و تفاوت آن با روش تعديل سالانه و بدون تعديل در سيستم چيست؟ استفاده از اين روش چه مزيتي دارد؟')), Ltrim(RTrim('روش هاي محاسبه ماليات در سيستم به سه روش تعديل ماهانه، تعديل سالانه و بدون تعديل انجام ميگيرد. در روش تعديل ماهانه در هر ماه، ميانگيني از تمامي درآمدهاي مشمول از ابتدا تا پايان ماه جاري محاسبه شده و با توجه به ماليات هاي پرداخت شده تا ماه قبل، ماليات ماه جاري محاسبه مي گردد. اما در روش تعديل سالانه كه تلفيقي از روش بدون تعديل و تعديل ماهانه مي باشد، حقوق هر ماه پرسنل در 11 ماه اول سال بطور مستقيم وارد جدول مالياتي شده و ماليات آن محاسبه مي گردد (مانند روش بدون تعديل) و در ماه آخر سال نيز مانند روش تعديل ماهانه تمامي درآمدهاي مشمول شخص از ابتدا تا پايان سال جمع شده و ميانگيني از ماليات قابل پرداخت محاسبه مي گردد، سپس ماليات محاسبه شده با ماليات واقعي پرداخت شده در ماه هاي قبل مقايسه شده و اختلاف (اضافه يا كسر) پرداخت به دست مي آيد. در روش بدون تعديل نيز بدون توجه به مالياتهاي پرداخت شده در ماه هاي قبل، درآمد مشمول هر ماه بطور مستقيم وارد جدول مالياتي شده و ماليات آن محاسبه مي گردد. اما مزيت روش تعديل ماهيانه نسبت به روش هاي ديگر در اين است كه ماليات پرداختني شما ماه به ماه تعديل ميشود و شما در طول سال ماليات بيشتري را نسبت به حقوق ساليانه پرداخت نمي كنيد.')), 0, 0, 519 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 243, Ltrim(RTrim('Payroll/Branches')), Ltrim(RTrim('شعب بيمه و ماليات')), Ltrim(RTrim('چگونه ميتوان شعبه  بيمه تامين اجتماعي  را تعريف نمود؟ ')), Ltrim(RTrim('جهت تعريف شعب بيمه و ماليات به ترتيب ذيل و در دو مرحله صورت ميگيرد:   مرحله اول: تعريف طرف حساب (عنوان شعبه بيمه تامين اجتماعي/بيمه تكميلي/وزارت دارايي)   1- از منوي عمودي وارد  شركت  شده و از قسمت عمليات گزينه  طرف حساب جديد  را انتخاب نمائيد.   2- در صفحه باز شده ابتدا نوع را  شخص حقوقي  قرار داده و سپس عنوان شعبه را يادداشت نمائيد.   مرحله دوم: تعريف جزئيات شعبه بيمه تامين اجتماعي/بيمه تكميلي/وزارت دارايي   1- از منوي عمودي وارد سيستم  حقوق و دستمزد  شده و از قسمت عمليات گزينه  شعبه بيمه و ماليات جديد  را انتخاب نمائيد.   2- جهت تعريف شعبه  بيمه تامين اجتماعي : از بالاي صفحه گزينه  تامين اجتماعي  را انتخاب كرده و سپس اطلاعات كد شركت/شماره پروندهه، شماره پيمان و نفرات معاف از بيمه را درج كرده و در نهايت فرم را ذخيره نمائيد.  ***توجه: در صورت وارد نمودن نفرات معاف از بيمه، مي بايست مركز هزينه مورد نظر را نيز تعيين نمود. ')), 0, 0, 520 )
