Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 299, Ltrim(RTrim('Config/Configuration')), Ltrim(RTrim('تنظيمات سپيدار')), Ltrim(RTrim('انجام تنظيمات ارسال پيامك ')), Ltrim(RTrim('شما قبل از شروع به كار با سيستم مديريت پيام ، بايد يك سري تنظيمات را در سيستم خود پياده سازي كنيد.  در ابتدا بايد در سيستم ايستگاه ارسال كننده پيام مشخص شود زيرا ممكن است تمامي ايستگاههاي كاري به اينترنت متصل نباشند در نتيجه بايد در قسمت تنظيمات مشخص كنيد كه آيا ايستگاه كاري جاري امكان اتصال به اينترنت و ارسال پيامك را دارد يا همه ايستگاههاي كاري به يك سيستم جهت ارسال پيامك متصل مي شوند.  براي اين كار    در قسمت تنظيمات نرم افزار گزينه تنظيمات را انتخاب نماييد.        سپس در تب  تنظيمات پيام  ايستگاه كاري مورد نظر خود جهت ارسال پيامك را انتخاب كنيد.        نكته: در صورتي كه سيستم شما تك كاربره است بروي گزينه  ايستگاه فرستنده پيام ايستگاه جاري باشد  كليك كنيد و در صورتي كه سيستم شما چند كاربره است  مي توانيد ايستگاه كاري مورد نظر خود را براي ارسال پيام با وارد كردن ادرس IP  ان و يا وارد كردن نام آن در قسمت ايستگاه كاري ارسال كننده پيام مشخص كنيد    در قسمت فاصله زماني ارسال پيامك شما مشخص مي كنيد در چه فواصل زماني پيامك از نرم افزار به مخابرات از طريق اينترنت منتقل شود       نكته : توجه به اين نكته ضروري است كه با تغيير اين زمان، بايد از برنامه خارج و دوباره برنامه را باز كنيد   در قسمت شماره فرستنده پيش فرض از بين شماره هاي خريداري شده، يك شماره را براي ارسال پيامك به صورت پيش فرض انتخاب مي نماييد.        و در صورت تمايل مي توانيد شماره فرستنده پيام خود را در صورت داشتن چند خط ار اين قسمت تغيير دهيد   شما بعد از انجام تنظيمات اوليه در سيستم مي توانيد شروع به ارسال و دريافت پيامك از طريق برنامه نماييد .   ')), 0, 0, 831 )
