Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 354, Ltrim(RTrim('Payroll/Element')), Ltrim(RTrim('عوامل حقوق')), Ltrim(RTrim('عوامل  فوق العاده  با عوامل  عادي  چه تفاوت هايي دارند؟ ')), Ltrim(RTrim('توضيحات عوامل  عادي/فوق العاده  به شرح ذيل مي باشد:   A. عادي: عواملي با ويژگي  عادي  عوامل اصلي سيستم بوده و در تمامي محاسبات (هزينه/كاركرد و ...) شركت داشته و در رويداد هاي مالي نيز تاثير مي گذارد.   B. فوق العاده: عوامل اصلي سيستم نيستند و فقط در محاسبه ديگر عوامل محاسباتي (اضافه كاري، نوبت كاري و ...) به عنوان مبناي محاسبه و تهيه گزارش ها نمايش داده ميشوند.  *عامل فوق العاده در فيش حقوقي، در سند حسابداري، در ديسكت بيمه و دارايي نشان داده نمي شود. گزارش عامل فوق العاده را در مرور حقوق و ليست هاي حقوق مي توان مشاهده نمود.* ')), 0, 0, 542 )
