Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 785, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim(' چگونه ميتوان براي طرف حسابي كه هم تامين كننده و هم مشتري است ، صورت حساب تهيه نمود به طوري كه هم گردش مربوط به نقش تامين كننده و هم گردش مربوط نقش مشتري آورده شود؟  ')), Ltrim(RTrim('در تهيه صورت حساب طرف مقابل بالاي فرم مي توانيد نوع صورت حساب را انتخاب كنيد. براي طرف حساب مورد نظر يك بار صورت حساب از نوع خريد و بار ديگر از نوع فروش صورت حساب تهيه كنيد. سپس بالاي فرم سلكتور پرينتر را باز كرده و گزارش صورتحساب خريد و فروش و يا خريد و فروش ريز اقلام استفاده نماييد. ')), 0, 0, 1128 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 786, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('نحوه تهيه صورتحساب طرف مقابل از سيستم مشتريان و فروش به چه صورت مي باشد؟ ')), Ltrim(RTrim('از منوي عمليات بر روي آيتم صورتحساب طرف مقابل جديد كليك كرده، پس از انتخاب نوع صورتحساب ( خريد يا فروش ) و طرف مقابل از سلكتور مربوطه، تاريخ را تا آن تاريخي كه ميخواهيم براي شخص صورتحساب بگيريم وارد ميكنيم. سپس از پايين صفحه بر روي آيكون نمايش اقلام كليك مي كنيم، تمام گردش هاي شخص تا آن تاريخ نمايش داده خواهد شد. براي اينكه بتوانيم از صورتحساب شخص پرينت بگيريم ابتدا بايد فرم را ذخيره كرده، سپس از بالاي پنجره پرينتر را باز كرده و فرمت چاپي موردنظر را انتخاب كنيم. ')), 0, 0, 752 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 787, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('هنگام ذخيره فاكتور فروش ويرايش شده سيستم خطا ميدهد  به دليل وجود صورت حساب براي مشتري امكان ويرايش و حذف فاكتور وجود ندارد  ، علت چيست؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم  مشتريان و فروش  شده از قسمت فهرست گزينه  صورت حساب هاي طرف مقابل  را باز كرده و صورت حسابي كه براي مشتري ثبت شده است را انتخاب كنيد.در صورت تمايل، روي آن كليك راست كرده و گزينه حذف را انتخاب كنيد.   سپس اقدام به ويرايش فاكتور نماييد. ')), 0, 0, 1924 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 788, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('چطور ميتوان صورت حساب طرف مقابل به تفكيك ارز پايه و ساير ارز ها صادر نمود؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم  مشتريان و فروش  يا  تامين كنندگان و انبار  شده و از قسمت عمليات گزينه  صورت حساب طرف مقابل جديد  را انتخاب نمائيد.   در فرم باز شده؛ پس از انتخاب طرف مقابل، از پايين صفحه گزينه  نمايش اقلام  را جهت فراخواني صورتحساب انتخاب نمائيد.   در فرم جاري، در صورت انتخاب سربرگ  اقلام  اطلاعات صورت حساب به ارز پايه را نمايش خواهد داد و در صورت انتخاب سربرگ  گردش به تفكيك ارز  گردش ارزي و ارز پايه صورت حساب را مي توان تهيه نمود. ')), 0, 0, 2352 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 789, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('بعد از ثبت و ذخيره عمليات چگونه ميتوان از عمليات انجام شده پرينت/گزارش چاپ تهيه نمود؟ ')), Ltrim(RTrim('در فرم عمليات ذخيره شده، از بالاي صفحه و از گزينه  چاپ  با توجه به نياز، يكي از گزارش هاي موجود را انتخاب كرده و گزارش چاپي تهيه نمائيد.   **نكته: گزارشات پيش فرض نرم افزار و گزارشات طراحي شده در اين قسمت قابل مشاهده خواهند بود. ')), 0, 0, 1279 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 790, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('علت مغايرت صورتحساب طرف مقابل با مرور حسابها در چيست ؟    ')), Ltrim(RTrim('در صورتيكه براي هريك از عملياتي كه در سيستم  مشتريان و فروش  ،  تامين كنندگان و انبار  و   دريافت و پرداخت   ثبت ميشود سند حسابداري نيز صادر شود، آن مانده به سيستم حسابداري هم منتقل ميشود و مانده ها در هر دو سيستم با هم يكي ميشود. اما وجود مغايرت در مانده ها به اين دليل است كه عملياتي در سيستم فروش يا انبار و يا دريافت و پرداخت ثبت كرده ايد كه سند حسابداري براي آن صادر نشده است، يا اينكه بعضي از عمليات مربوط  تنها در سيستم حسابداري بصورت دستي ثبت شده است و در نتيجه در صورتحساب طرف مقابل اعمال نشده است.   همچنين در صورتحساب طرف مقابل، تنها عمليات مربوط به تامين كننده و مشتري نمايش داده ميشود. يعني به طور مثال رسيد دريافت از نوع ساير در اين فرم نمايش داده نميشود. ')), 0, 0, 1265 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 791, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('آيا ميتوانم صورت حساب طرف مقابل را بصورت هم ريالي و هم ارزي صادر كنم؟ ')), Ltrim(RTrim('در فرم صورت حساب طرف مقايل جديد دو تب با عنوان اقلام و گردش به تفكيك ارز وجود دارد كه ميتوان از تب اول جهت گردش ريالي و از تب دوم جهت گردش ارزي و ارز پايه صورت حساب تهيه نمود. ')), 0, 0, 1489 )
