Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 802, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('چگونه ميتوان از مانده حساب كليه مشتريان و تامين كنندگان بصورت يكجا گزارش گرفت؟')), Ltrim(RTrim('كاربر ميتواند صورت حساب تمامي مشتريان و تامين كنندگان را به صورت مانده در يك گزارش مشاهده نمايد :  از  قسمت شركت منوي فهرست، گزارش ساز را باز كرده و از بخش مشتريان و فروش "گزارش صورت حساب مشتريان" را انتخاب مي كنيم، سپس در پنجره باز شده از پايين كليد تاييد را فشار مي دهيم.')), 0, 0, 401 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 803, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('انواع فرمت هاي چاپ در صورتحساب طرف مقابل را توضيح دهيد.')), Ltrim(RTrim(' پس از ذخيره كردن صورت حساب طرف مقابل از بالاي صفحه بر روي آيكون پرينتر كليك كرده و انواع گزارشات را مشاهده مي كنيم :   1- گزارش صورت حساب طرف مقابل : براساس نوع فروش و يا خريد انتخاب شده، ميتوانيد گزارش فعاليت مشتري يا تامين كننده را به طور خلاصه مشاهده نمائيد.   2- گزارش صورت حساب به ريز اقلام : در اين گزارش تمامي فعاليت هاي مشتري يا تامين كننده به ريز نمايش داده مي شود. در صورتي گزارش را به شما نمايش مي دهد كه قبل از تاييدگزارش در قسمت كد تفصيل شماره تفصيلي مشتري يا تامين كننده را وارد كرده باشيد.   3- گزارش صورت حساب خريد و فروش : در صورتي كه طرف حساب شما هم مشتري و هم تامين كننده باشد، يكبار گزارش را براساس فروش و يكبار گزارش را  براساس خريد ذخيره ميكنيم، سپس با استفاده از اين فرمت چاپ ميتوانيم گزارش تمام خريد و فروش هاي آن طرف حساب را مشاهده نماييم.   4- گزارش صورت حساب مشتريان : اگر بخواهيم مانده حساب كليه مشتريان و تامين كنندگان را به صورت يكجا گزارش بگيريم از اين گزارش استفاده مي كنيم. 5- گزارش صورتحساب خريد و فروش به ريز اقلام : در صورتي كه طرف حساب شما هم مشتري و هم تامين كننده باشد، در اين گزارش ميتوان تمامي خريد و فروش هاي آن طرف حساب را به ريز اقلام مشاهده نمود.')), 0, 0, 402 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 804, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('كاربرد گزارش صورتحساب طرف مقابل چيست؟')), Ltrim(RTrim('گزارش صورت حساب براي بدست آوردن مانده حساب مشتريان و تامين كنندگان تا تاريخي مشخص مورد استفاده قرار ميگيرد.   نكته : گزارش صورتحساب در صورتي مي تواند مانده واقعي مشتري و يا تامين كننده را نمايش دهد كه كليه عمليات مربوط به خريد و فروش ها، دريافت و پرداخت ها و اعلاميه بدهكار و بستانكار در قسمت ها و فرم هاي مربوط به خود ثبت شده باشند .   به طور مثال كاربري كه داراي سيستم دريافت و پرداخت است حتما بايد دريافتي ها و پرداختي هاي خود را در سيستم دريافت و پرداخت ثبت كند و از ثبت دستي سند در سيستم حسابداري خودداري نمايد.')), 0, 0, 395 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 805, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('مراحل تهيه گزارش صورتحساب طرف مقابل به چه صورت است؟')), Ltrim(RTrim('براي ثبت صورت حساب طرف مقابل، به سيستم مشتريان و فروش رفته و از منوي عمليات بر روي گزينه صورتحساب طرف مقابل كليك ميكنيم. در پنجره باز شده :   1- نوع خريد و يا فروش را مشخص مي كنيم.   2- طرف مقابل مشتري و يا تامين كننده را انتخاب مي كنيم.   3- محدوده ي تاريخي كه تا آن تاريخ ميخواهيم از مانده مشتري گزارش بگيريم وارد مي كنيم.   4- از پايين صفجه كليد نمايش اقلام را فشار داده و سپس فرم را ذخيره مي كنيم.   5- از بالاي صفحه با استفاده از آيكون پرينتر ميتوانيم گزارش صورت حساب طرف مقابل به ريز اقلام، صورتحساب خريد و فروش و يا صورتحساب مشتريان را مشاهده نمائيم.')), 0, 0, 396 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 806, Ltrim(RTrim('General/Bill')), Ltrim(RTrim('صورت حساب طرف مقابل')), Ltrim(RTrim('در سطر اول صورتحساب طرف مقابل نام مشتري را نمايش نميدهد.')), Ltrim(RTrim('در صورتي كه براي مشتري اعلاميه بدهكار و بستانكار ثبت شده باشد كه در آن معيني تفصيل ناپذير انتخاب شده باشد در اين صورت نام مشتري در صورتحساب طرف مقابل نمايش داده نميشود.')), 0, 0, 579 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 807, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان تنظيمات ستون ها در فرم ها و ليست ها را به حالت پيش فرض بازگرداند؟ ')), Ltrim(RTrim('جهت بازگرداندن موارد مخفي شده، مي بايست در فرم مورد نظر از پايين صفحه گزينه  انتخابگر ستون  را انتخاب كرده و سپس با انتخاب  بازگرداندن تغييرات ستونها به حالت پيش فرض  تغييرات پيش فرض را بازيابي نمود  ***توجه: در صورتي كه لازم باشد تنها تعدادي از ستون ها فراخواني شود، پس از انتخاب گزينه انتخابگر ستون در ليست باز شده مواردي كه لازم مي باشد را دوبار كليك كرده تا به ليست اضافه گردد. ')), 0, 0, 2761 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 808, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('موقع انتخاب كالا در داخل فاكتور فروش/پيش فاكتور، رديابي مورد نظر براي آن كالا نمايش داده نمي شود، علت چيست؟ ')), Ltrim(RTrim('اگر داخل فاكتور فروش/ پيش فاكتور، رديابي كالا نمايش داده نشود موارد زير را بايستي كنترل نمود:   1. رديابي مورد نظر در سيستم وارد نشده است (رسيد انبار آن كالا با رديابي مورد نظر ثبت نشده است )   2. موجودي رديابي مورد نظر صفر شده و در تنظيمات برنامه تب مشتريان و فروش در قسمت كنترل هاي فروش، تيك گزينه   رديابي با موجودي صفر نمايش داده شود   برداشته شده است.   3. رديابي مورد نظر در سال قبل ثبت شده اما هنوز سال قبل بسته نشده و رديابي ها به سال جديد منتقل نشده اند. ')), 0, 0, 2693 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 809, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('آيا اين مكان در سيستم وجود دارد كه با زدن پرينتر و انتخاب نمونه، مستقيم و بدون پيش نمايش پرينت انجام شود و اينگونه نباشيد كه از سلكتور پرينتر فرمت مورد نظر را انتخاب كرد، راهكار چيست؟ ')), Ltrim(RTrim('در قسمت شركت،گزارش ساز سيستم مشتريان و فروش را باز كرده روي فرمت مورد نظر كه ميخواهيم مستقيم چاپ شود راست كليك كرده و تكثير گزارش  زده سپس در صفحه  تيك گزارش مستقيم چاپ شود را فعال كرده و ذخيره مينماييم درنهايت در فاكتور فروش با كليك روي علامت پرينتر گزارش مستقيم چاپ ميشود. ')), 0, 0, 2701 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 810, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('نحوه فروش  بسته محصول  به چه شكل است؟ ')), Ltrim(RTrim('جهت به فروش رساندن  بسته محصول  از منوي عمودي وارد سيستم  مشتريان و فروش  شده  فاكتور فروش جديد  را باز كنيد، پس از تكميل قسمت بالاي فرم،از پايين صفحه روي گزينه  بسته محصول  كليك نماييد، در صفحه باز شدن سطر جديد ايجاد كرده بسته محصول و تعداد مورد نظر را انتخاب كرده دكمه  تاييد  را كليك كنيد. در انتها فرم را از بالاي صفحه  ذخيره  نماييد. ')), 0, 0, 2649 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 811, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چنانچه تخفيف تعريف شده از نوع  بسته محصول  و  سرجمع فاكتورها  در فاكتورهاي فروش لحاظ نشود علت چيست؟ ')), Ltrim(RTrim('در صورتي كه تخفيفاتي با مبناي محاسبه  بسته محصول  و يا   سرجمع فاكتورها   تعريف نموده ايد، جهت اعمال تخفيف مورد نظر در فاكتور فروش، بايستي پس از انتخاب اقلام مورد فروش، از داخل فاكتور فروش و پايين صفحه، گزينه  محاسبه تخفيف  را انتخاب كرده فرم را از بالاي صفحه  ذخيره  نماييد. ')), 0, 0, 2646 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 812, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('در برخي موارد پس از ثبت تخفيف كلي براي برخي اقلام تخفيف مورد نظر روي قلم مورد نظر لحاظ نميشود، علت چيست؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم  مشتريان و فروش  شده از قسمت عمليات گزينه  اعلاميه قيمت  را باز كرده، براي كالاي مورد نظر در نوع فروش مورد استفاده در فرم پيش فاكتور/فاكتور فروش/فاكتور برگشتي، تيك گزينه  امكان تغيير تخفيف در فاكتور  را گذاشته فرم را ذخيره نماييد. ')), 0, 0, 2664 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 813, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('در مواقعي كه پيش فاكتور مورد نظر در سلكتور مربوطه داخل فاكتور فروش مشاهده نميشود، چه مواردي را بايد كنترل نمود؟ ')), Ltrim(RTrim('عدم نمايش پيش فاكتور مورد نظر در سلكتور فرم فاكتور فروش ميتواند ناشي از موارد زير باشد:   1. پيش فاكتور مورد نظر قبلا تبديل به فاكتور شده باشد.   2. تاريخ اعتبار پيش فاكتور به اتمام رسيده باشد.   3. نام مشتري انتخاب شده در فرم فاكتور فروش با پيش فاكتور مورد نظر مغاير باشد   4. وضعيت پيش فاكتور خاتمه يافته باشد.   5. نوع فروش انتخاب شده در فاكتور فروش با پيش فاكتور مغاير باشد. ')), 0, 0, 2679 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 814, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('آيا امكان كپي كردن فاكتور فروش در سيستم وجود دارد؟ ')), Ltrim(RTrim('براي تكثير فاكتور فروش به دو روش مي توان اقدام نمود:  1. در منوي عمودي وارد سيستم  مشتريان و فروش شده  از قسمت فهرست گزينه  فاكتورهاي فروش  را انتخاب نمائيد.   روي فاكتور مورد نظر راست كليك كرده و گزينه تكثير را انتخاب نمائيد.   2. از فهرست فاكتورهاي فروش، فاكتور فروش را باز كرده و از بالاي فرم آيكن  كپي  را انتخاب نمائيد. ')), 0, 0, 2588 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 815, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('در هر فرمي از سيستم، در قسمت اطلاعات تكميلي سطرهاي ممكن جهت ثبت اطلاعات ديده نمي شود، علت چيست؟ ')), Ltrim(RTrim('دسترسي مشاهده رديف هاي اطلاعات تكميلي براي كاربر سرپرست وجود دارد. پس از ثبت اطلاعات توسط كاربر سرپرست، نمايش سطرها براي ساير كاربران فعال خواهد شد. ')), 0, 0, 2602 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 816, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('آيا امكان مشاهده مبالغ به ارز پايه در فاكتور فروش ارزي وجود دارد؟ ')), Ltrim(RTrim('بله،   ابتدا ميبايست برنامه خود را به آخرين نسخه بروزرساني كنيد.   سپس بعد از ثبت فاكتور فروش ارزي، در قسمت پايين فرم سربرگي به نام  مبالغ به ارز پايه  اضافه ميشود كه علاوه بر مبالغ ارزي امكان مشاهده تمامي مبالغ فاكتور اعم از كل و تخفيف و ماليات و عوارض به ارز پايه را نيز فراهم ميكند. ')), 0, 0, 2613 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 817, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان از عمليات بدون  ماليات و عوارض ارزش افزوده  در سيستم(هاي) (تامين كنندگان و انبار/مشتريان و فروش) گزارش تهيه نمود؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم(هاي) (تامين كنندگان و انبار/مشتريان و فروش) شده و در صورتي كه هدف كاربر تنها مشاهده اين عمليات باشد، مي بايست:   در منوي عمودي از قسمت عمليات گزينه  مرور(مبلغي انبار/مرور فروش)  را انتخاب كنيد.   بدون فيلتر كردن در هيچ يك از سربرگ ها، سربرگ  پيش فاكتور/فاكتور خريد/اسناد/اسناد فروش  را انتخاب كرده از ستون  ماليات و عوارض  گزينه  بزرگتر از  را فيلتر كرده و عدد صفر  را يادداشت كنيد.  ***توجه: در صورتي كه ستون هاي مذكور وجود نداشت، از پايين صفحه گزينه انتخابگر ستون را انتخاب كرده و از قسمت  انتخابگر ستون  در ليست باز شده مواردي كه لازم مي باشد را دوبار كليك كرده تا به ليست اضافه گردد. ')), 0, 0, 2616 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 818, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان ستون باركد را به فرم تعريف (پيش فاكتور، فاكتور فروش و ...) اضافه نمود؟  ')), Ltrim(RTrim('از منوي عمودي وارد قسمت  تنظيمات  شده از قسمت عمليات گزينه  تنظيمات  را انتخاب كنيد.   در صفحه باز شده از سربرگ  مشتريان و فروش  از قسمت باركد گزينه  نمايش باركد كالا در اسناد فروش  را فعال كرده و فرم را ذخيره فرمائيد. ')), 0, 0, 2789 )
