Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 820, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('امكان تكثير (كپي) كردن، براي كدام عمليات امكان پذير مي باشد؟ ')), Ltrim(RTrim('امكان تكثير(كپي) كردن عمليات در هر سيستم به شرح ذيل مي باشد:   1- سيستم مشتريان و فروش:   پيش فاكتور، فاكتور فروش، پورسانت، اعلاميه قيمت   2- سيستم تامين كنندگان و انبار:   فاكتور خريد خدمات، رسيد انبار، خروج انبار، فاكتور خريد   3- سيستم سفارش كار:   فرمول ساخت   4- سيستم دارايي ثابت:   فاكتور خريد دارايي، اقلام در فرم تحصيل دارايي   5- سيستم حسابداري:   سند حسابداري   6- سيستم حقوق و دستمزد:   اطلاعات ماهانه، قرارداد كار   7- عمومي - شركت:   گزارشات پيش فرض و از قبل طرحي شده در  گزارش ساز ، گزارشات طراحي شده  گزارشات پويا ، الگو هاي پيش فرض مديريت پيام ')), 0, 0, 2846 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 821, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('  چگونه ميتوان در فاكتور فروش براي كالايي كه داراي سريال است سريال هارا ثبت كرد؟ ')), Ltrim(RTrim('    در فاكتور فروش جديد پس از انتخاب كالا گزينه ي  ثبت سريالي پايين صفحه سمت چپ كليك كرده و در كادر باز شده سريال هاي كالا را بصورت دستي و يا از باركد اسكنر و يا از فايل متني دريافت كرده و گزينه ي ثبت را كليك كرده توجه فرماييد ميبايست تعداد سريال هاي وارد شده با تعداد واحد اصلي كالاي مورد نظر برابر باشد. همچنين در صورت صدور خروج از فاكتور فروش در خروج انبار سريال هاي كالا مشاهده ميشود.   ')), 0, 0, 2860 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 822, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('در چه شرايطي هنگام صدور (فاكتور فروش/پيش فاكتور و ...) نام  مشتري  در ستون مشتريان قابل مشاهده نمي باشد؟ ')), Ltrim(RTrim('در صورتي كه يكي از شرايط ذيل رعايت نشده باشد، هنگام ثبت عمليات (فاكتور فروش/پيش فاكتور و ...) عنوان مشتري در ليست طرف حسابها قابل مشاهده نخواهد بود:   1- تفصيلي طرف حساب مورد نظر، از قبل تعريف نشده است.   2- عنوان مشتري مورد نظر، از قبل به عنوان تفصيلي (ساير/مركز هزينه و ...) تعريف شده است.   3- در تعريف تفصيلي طرف حساب مورد نظر، تيك گزينه  مشتري  انتخاب نشده است.   4- طرف حساب مورد نظر غير فعال مي باشد. ')), 0, 0, 2870 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 823, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('بعد از رعايت چه مواردي ميتوان فاكتور فروش را  حذف  نمود؟ ')), Ltrim(RTrim('در صورتي ميتوان  حذف فاكتور فروش  را انجام داد كه شرايط زير رعايت شده باشد:   1- اسناد حسابداري در وضعيت  موقت  باشد.   2- بر مبناي فاكتور فروش سند خروج انبار صادر نشده باشد.   3- بر مبناي فاكتور فروش رسيد دريافت/تسويه حساب طرف مقابل صادر نشده باشد.   4- بر مبناي فاكتور فروش فاكتور برگشتي صادر نشده باشد.   5- در صورتي كه در بازه تاريخ فاكتور فروش، براي مشتري صورتحساب صادر شده باشد، مي بايست حذف گردد.   6- در صورتي كه از قسمت شركت، عمليات پايان سال، وضعيت سيستم  مشتريان و فروش   بسته  باشد، مي بايست وضعيت سيستم مذكور  باز  شود.   7- در صورتي كه در بازه تاريخ فاكتور فروش، فايل خريد و فروش فصلي و ماليات بر ارزش افزوده تهيه شده باشد مي بايست آنها را حذف كرد.   8- در صورتي كه از زير سيستم فروش پيشرفته استفاده شود و داخل فاكتور فروش از بازارياب استفاده شده باشد، در بازه زماني فاكتور فروش  محاسبه پورسانت  انجام گرفته باشد مي بايست حذف گردد.   9-  براي  فاكتور فروش تقسيط انجام نشده باشد. ')), 0, 0, 2925 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 824, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim(' عمليات  فاكتور فروش  به چند نوع ميتوان گزارش چاپي تهيه كرد؟ ')), Ltrim(RTrim('به هشت نوع:   1- فاكتور نمونه 1 به ارز پايه: در قالب فاكتور افقي A4 به ارز پايه سيستم صادر ميگردد.   2- فاكتور نمونه 1: در قالب فاكتور افقي A4 به ارز فاكتوردر  سيستم صادر ميگردد.   3- فاكتور نمونه با سريال اقلام: در قالب فاكتور افقي A4 به ارز فاكتور و به همراه اطلاعات سريال هر قلم صادر ميگردد.   4- فاكتور نمونه با مشخصات كالا: در قالب فاكتور افقي A4 به ارز فاكتور و به همراه مشخصات هر كالا صادر ميگردد.   5- فاكتور نمونه 2 به ارز پايه: در قالب فاكتور هشت سانتي به ارز فاكتور صادر مي گردد.   6- فاكتور نمونه 2: در قالب فاكتور افقي A4 به ارز فاكتور صادر ميگردد.   7- فاكتور A5: در قالب فاكتور افقي A5 به ارز فاكتور صادر ميگردد.   8- فاكتور عمودي A4: در قالب فاكتور عمودي A4 به ارز فاكتور صادر ميگردد. ')), 0, 0, 2946 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 825, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('هنگام صدور فاكتور فروش براي يك كالا با پيغام  مقدارموجودي انبار كالاي مرتبط نميتواند منفي باشد  مشاهده ميگردد، علت چيست؟ ')), Ltrim(RTrim('به دليل اينكه ميزان موجودي كالاي نام برده در انبار انتخاب شده در فاكتور فروش، كمتر از ميزان در نظر گرفته شده براي فروش است. در صورتي كه ميخواهيد با وجود عدم موجودي فاكتور فروش خود را صادر كنيد، كافي است از قسمت  تنظيمات  وارد تب  تامين كنندگان و انبار  شده تيك گزينه  موجودي منفي در انبار كنترل شود  را غير فعال نماييد. ')), 0, 0, 3281 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 826, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان در فاكتور فروش سريال كالا ثبت نمود؟ ')), Ltrim(RTrim('در فاكتور فروش جديد پس از انتخاب كالا گزينه  ثبت سريالي  پايين صفحه سمت چپ را كليك كرده و در كادر باز شده سريال هاي كالا را بصورت دستي و يا از باركد اسكنر و يا از فايل متني دريافت كرده و گزينه ثبت را كليك كنيد.   توجه فرماييد ميبايست تعداد سريال هاي وارد شده با تعداد واحد اصلي كالاي مورد نظر برابر باشد، همچنين در صورت صدور خروج انبار از روي فاكتور فروش، سريال هاي وارد شده در خروج انبار نيز مشاهده خواهند شد. ')), 0, 0, 3370 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 827, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('آيا امكان جابجا كردن رديف هاي داخل فاكتور فروش وجود دارد؟ ')), Ltrim(RTrim('بله،   به منظور انتقال سطرهاي داخل فرم فاكتور فروش به بالا يا پايين، روي سطر مورد نظر كليك نموده از پايين فرم با استفاده از علامت فلش هاي آبي رنگ (انتقال به بالا و پايين)، ميتوانيد سطر مورد نظر را جا به جا نماييد.   لازم به ذكر است اين امكان در زير سيستم فروش پيشرفته در دسترس است. ')), 0, 0, 3371 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 828, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('منظور از گزينه  توافقات تسويه  در فاكتور فروش چيست؟ ')), Ltrim(RTrim('نحوه توافق انجام شده بين فروشنده و مشتري را ميتوان در اين قسمت درج نمود.   لازم به ذكر است اين گزينه تنها در قفل هايي كه داراي سيستم  سفارش گيري و پخش  هستند موجود است. ')), 0, 0, 3372 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 829, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('منظور از گزينه  كنترل اعتبار مشتري با در نظر گرفتن وصول چك  چيست و چه كاربردي دارد؟ ')), Ltrim(RTrim('كاربرد گزينه  كنترل اعتبار مشتري با در نظر گرفتن وصول چك  با ذكر يك نمونه به شرح زير ميباشد:   فرض كنيد براي يك مشتري سقف اعتباري به مبلغ  2000000 ريال در نظر گرفته شده است، و فاكتور فروشي به همين مبلغ براي ايشان ثبت شده است، سپس مشتري يك فقره چك به مبلغ  ريال2000000 بابت تسويه فاكتور مذكور پرداخت ميكند.   چنانچه تيك گزينه  كنترل اعتبار مشتري با در نظر گرفتن وصول چك  در قسمت تنظيمات، تنظيمات،تب مشتريان فروش، سربرگ كنترل هاي اسناد فروش  غير فعال  باشد، چك دريافت شده از مشتري بدون در نظر گرفتن احتمال وصول و يا عدم وصول، روي سقف اعتبار مشتري تاثير گذاشته و اجازه ثبت فروش مجدد به او را خواهد داد.   حال چنانچه تيگ گزينه  كنترل اعتبار مشتري با در نظر گرفتن وصول چك ،  فعال  باشد، تنها زماني كه چك دريافتي به وضعيت وصول برسد مبلغ ان روي سقف اعتبار طرف مقابل تاثير خواهد گذاشت.   به عبارتي در صورت فعال بودن تيك مورد نظر، امكان ثبت فروش به مشتريان، بيش از سقف اعتبار تعيين شده براي آنها، تا زمان ثبت وصول چك دريافت شده از مشتريان، وجود نخواهد داشت.   در نظر داشته باشيد اين امكان همانند گزينه سقف اعتبار، تنها در قفل هايي كه داراي زير سيستم فروش پيشرفته هستند وجود دارد. ')), 0, 0, 3373 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 830, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('تاريخ نمايش داده شده داخل فرم فاكتور صحيح نميباشد، علت چيست؟ ')), Ltrim(RTrim('تاريخ فاكتور فروش از روي تاريخ ويندوز خوانده ميشود بنابراين با تصحيح تاريخ ويندوز تاريخ فاكتور نيز اصلاح خواهد شد. ')), 0, 0, 596 )
