Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 842, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('نحوه ثبت انواع هزينه ها از قبيل هزينه پيك در فاكتور فروش به چه صورت است؟ ')), Ltrim(RTrim('اين كار به دو روش امكان پذير است:   1- در داخل فاكتور فروش از قسمت پايين فرم گزينه  اضافات  را براي ثبت هزينه خود در نظر بگيريد.   2- در منوي عمودي وارد سيستم  تامين كنندگان و انبار  شده از قسمت عمليات گزينه  كالا/خدمت جديد  را انتخاب نماييد. در صفحه باز شده از بالاي فرم تيك گزينه  خدمت  را فعال كرده هزينه مورد نظر را در قالب يك خدمت تعريف كنيد. سپس آن را به عنوان يك قلم به فاكتورفروش خود اضافه نماييد.   ***در صورتي كه نميخواهيد هزينه مورد نظر مشمول  ماليات و عوارض  باشد، مي بايست از روش دوم ذكر شده استفاده كرده خدمت تعريف شده را معاف از ماليات و عوارض نماييد. ')), 0, 0, 381 )
