Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 845, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('تنظيمات مربوط به صدور خودكار سند حسابداري پس از ثبت فاكتور فروش، از كدام قسمت انجام مي شود؟ ')), Ltrim(RTrim('در منوي عمودي وارد ماژول  تنظيمات  شده از قسمت عمليات مجدد تنظيمات را انتخاب نماييد. در صفحه باز شده وارد تب  مشتريان و فروش  شده و در بخش  صدور خودكار سند حسابداري ، در صورتي كه تيك مربوط به گزينه  فاكتور فروش  را غير فعال كنيد سند صادر نخواهد شد و در صورتي كه تيك مربوطه فعال شود، سند حسابداري به صورت خودكار صادرخواهد شد. ')), 0, 0, 384 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 846, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان فاكتور فروش را  ويرايش  نمود؟ ')), Ltrim(RTrim('به منظور  ويرايش  يك فاكتور فروش مي بايست شرايط زير رعايت شده باشد:   1- سند حسابداري فاكتور فروش حذف شده باشد.   2- تسويه حساب فاكتور فروش حذف شده باشد.   3- در صورتي كه  خروج انبار  صادر شده باشد بايد خروج انبار مورد نظر حذف گردد.   4- در صورتي كه در بازه تاريخ فاكتور فروش، عمليات قيمت گذاري اسناد انبار انجام شده باشد، مي بايست قيمت گذاري مورد نظر برگشت از محاسبه گردد.   5- در صورتي كه در بازه تاريخ فاكتور فروش، عمليات انبارگرداني تا مرحله  تائيد شمارش  انجام شده باشد، مي بايست برگشت از تائيد شمارش انجام گيرد.   6- در صورتي كه در بازه تاريخ فاكتور فروش، براي مشتري صورتحساب صادر شده باشد، مي بايست حذف گردد.    7- در صورت وجود تقسيط براي فاكتور فروش، تقسيط ايجاد شده بايد حذف گردد.   8- در صورتي كه در بازه تاريخ فاكتور فروش،  فايل خريد و فروش فصلي  و  ماليات بر ارزش افزوده  تهيه شده باشد مي بايست آنها را حذف كرد.   9- چنانچه فاكتور مورد نظر در  توزيع گرم  و يا   توزيع سرد  شركت كرده باشد ميبايست حذف گردد. ')), 0, 0, 389 )
