Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 851, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('هنگام ثبت فاكتور فروش پيغام  نميتوان به تاريخ قبل از آخرين فاكتور صادر شده فاكتور جديد ثبت نمود  مشاهده ميشود، علت چيست؟ ')), Ltrim(RTrim('به منظور لغو اين كنترل در منوي عمودي وارد ماژول  تنظيمات  شده از قسمت عمليات مجدد تنظيمات  را انتخاب كرده وارد تب  مشتريان و فروش  شده تيك گزينه  كنترل تاريخ در اسناد فروش انجام شود  را غيرفعال نماييد. ')), 0, 0, 421 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 852, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('تفاوت موجودي با موجودي قابل فروش در چيست؟')), Ltrim(RTrim('موجودي قابل فروش: عبارت است از موجودي كل انبار با كسر موجودي فاكتورهاي فروشي كه هنوز براي انها خروج انبار صادر نشده است، يعني كالاهايي كه فروخته شده اند ولي هنوز از انبار خارج نشده اند.   موجودي انبار : موجودي واقعي انبار را به ما نشان مي دهد. عبارت است از موجودي كالاهايي كه فاكتور نشده اند + موجودي كالاهايي كه فاكتور شده اند اما هنوز از انبار خارج نشده اند.')), 0, 0, 422 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 853, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('دليل عدم محاسبه خودكار  ماليات و عوارض  براي اقلام فاكتور فروش چيست؟ ')), Ltrim(RTrim('چنانچه براي اقلام انتخاب شده در فاكتور فروش ماليات و عوارض محاسبه نگردد مي بايست موارد زير كنترل شوند:   1- در زمان تعريف كالا/خدمت براي آن تيك گزينه  معاف از ماليات و عوارض فروش  فعال شده است.   2-  نوع فروش  انتخاب  شده در فاكتور فروش از نوع فروش  صادراتي  است كه از ماليات و عوارض معاف است.   3- در  تنظيمات  سيستم در تب  عمومي ، تيك گزينه  ماليات بر ارزش افزوده  را فعال نكرده و يا نرخ هاي مربوطه وارد نشده اند. ')), 0, 0, 423 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 854, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('مانده حساب مشتري در فاكتور فروش با مرور حسابها مغايرت دارد، علت چيست؟ ')), Ltrim(RTrim('در نظر داشته باشيد ارتباط ميان ساير سيستم ها با سيستم حسابداري بصورت يكطرفه است.   بدين معني كه وقتي عملياتي در سيستم فروش ثبت ميشود و سند حسابداري آن بصورت دستي يا اتوماتيك صادر ميگردد، اثر آن در سيستم حسابداري نيز قابل پيگيري است و اين در حاليست كه وقتي ثبتي بصورت دستي در سيستم حسابداري انجام ميشود، اثري بر روي سيستم فروش نخواهد داشت.   بنابراين در صورت بروز چنين مغايرتي ميبايست موارد زير كنترل شود:   1- براي مشتري مورد نظر از سند دستي استفاده نكرده باشيد.   2- براي تمامي عمليات ثبت شده در نقش  مشتري  سند حسابداري صادر شده باشد.   3- دريافت و پرداخت هاي مشتري مورد نظر، با نقش  مشتري  ثبت شده باشند. ')), 0, 0, 424 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 855, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان فاكتور فروش را  حذف  نمود؟ ')), Ltrim(RTrim('در صورتي ميتوان  ويرايش فاكتور فروش  را انجام داد كه شرايط زير رعايت شده باشد:  1- سند حسابداري فاكتور فروش حذف شده باشد.   2- تسويه حساب فاكتور فروش حذف شده باشد.   3- در صورتي كه  خروج انبار  صادر شده باشد بايد خروج انبار مورد نظر حذف گردد.   4- در صورتي كه در بازه تاريخ فاكتور فروش، عمليات قيمت گذاري اسناد انبار انجام شده باشد، مي بايست قيمت گذاري مورد نظر برگشت از محاسبه صورت گيرد.   5- در صورتي كه در بازه تاريخ فاكتور فروش، عمليات انبارگرداني تا مرحله  تائيد شمارش  انجام شده باشد، مي بايست برگشت از تائيد شمارش انجام گيرد.   6- در صورتي كه در بازه تاريخ فاكتور فروش، براي مشتري  صورتحساب  صادر شده باشد، مي بايست حذف گردد.   7- در صورت وجود  تقسيط  براي فاكتور فروش، تقسيط ايجاد شده حذف گردد.   8- در صورتي كه در بازه تاريخ فاكتور فروش،  فايل خريد و فروش فصلي  و  ماليات بر ارزش افزوده  تهيه شده باشد مي بايست آنها را حذف كرد.   9- چنانچه فاكتور مورد نظر در  توزيع گرم  و يا   توزيع سرد  شركت كرده باشد ميبايست حذف گردد. ')), 0, 0, 426 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 856, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('آيا امكان اعمال قيمت ها بصورت اتوماتيك در فاكتور فروش وجود دارد؟ ')), Ltrim(RTrim('بله، جهت اعمال خودكار قيمت اقلام در فاكتور فروش، به دو روش ميتوان تنظيمات را انجام داد:   1- در منوي عمودي وارد سيستم  تامين كنندگان وانبار  شده از قسمت عمليات گزينه  كالا/خدمت جديد  را انتخاب نماييد.   در پنجره تعريف كالا/خدمت در تب  قيمت فروش ، با ايجاد يك رديف و ورود كد نوع فروش ميتوان مبلغ فروش و درصد اضافات و تخفيف را براي آن كالا مشخص نمود.   2- در منوي عمودي وارد سيستم  مشتريان و فروش  شده از قسمت عمليات گزينه  اعلاميه قيمت  را باز كرده با استفاده از علامت (+) پايين فرم سطر جديد ايجاد كرده پس از انتخاب نوع فروش و قلم مورد نظر ميتوانيد بصورت يكجا و براي تمامي كالاها مبلغ و مقادير اضافات و تخفيف كالا را مشخص نماييد. ')), 0, 0, 427 )
