Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 862, Ltrim(RTrim('Sales/Invoice')), Ltrim(RTrim('فاكتور فروش')), Ltrim(RTrim('چگونه ميتوان در فاكتور فروش  تخفيف كلي  اعمال نمود؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم  مشتريان و فروش  شده از قسمت عمليات گزينه  فاكتورفروش جديد  را بازكرده اطلاعات فاكتور و كليه اقلام فاكتور را وارد نماييد. درپايين فرم، گزينه اي به نام  تخفيف كلي  وجود دارد، با كليك بر روي آن ميتوان  نرخ يا مبلغ  تخفيف را مشخص كرد.  در نظر داشته باشيد اين تخفيف روي تمام اقلام فاكتور فروش تسهيم خواهد شد. ')), 0, 0, 973 )
