Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1038, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('براي طرف حساب ميتوان مانده ابتداي دوره ارزي وارد كرد؟ ')), Ltrim(RTrim('خير، نمي توان براي طرف حساب مانده ارزي وارد كرد.  اما ميتوان بعد از صدور سند افتتاحيه، سند را ويرايش كرده و در فيلد ارز، مبلغ مورد نظر را وارد نماييد.  توجه داشته باشيد، معين انتخاب شده براي تفصيلي مورد نظر بايد تفصيل پذير باشد. ')), 0, 0, 2185 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1039, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('در صورتي كه سيستم دارايي ثابت نباشد ، در سيستم چگونه ميتوان خريد دارايي ثابت ثبت كرد؟ ')), Ltrim(RTrim('براي خريد دارايي (اموال) از ثبت سند حسابداري دستي استفاده نماييد. همچنين ميتوانيد براي هر دارايي، يك تفصيلي از نوع ساير از قسمت حسابداري، گزينه تفصيلي ساير جديد را باز كرده و تعريف نماييد. ')), 0, 0, 2275 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1040, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('چگونه ميتوان تنظيماتي روي سند حسابداري اعمال كرد كه در پرينت سند ، شماره فرعي نمايش داده نشود؟ ')), Ltrim(RTrim('از منوي شركت، قسمت فهرست، فرم گزارش ساز را باز كنيد.+ حسابداري را باز كرده روي سند حسابداري راست كليك كرده و تكثير گزارش را انتخاب كنيد. در صفحه باز شده از بالاي فرم گزينه  طراحي  را زده و در صفحه طراحي سند فيلد شماره فرعي  را انتخاب كرده و حذف كنيد. بعد از انجام تغييرات مورد نظر، صفحه را ببنديد و پيغام ذخيره را خير انتخاب كنيد. در صفحه نمايش داده شده، كليد ذخيره را انتخاب نماييد. ')), 0, 0, 2268 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1041, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('چگونه ميتوان مانده اول دوره تنخواه گردان ريالي را در سيستم ثبت كرد؟ آيا مانده اول دوره بايد به صورت سيستمي ثبت گردد؟ ')), Ltrim(RTrim('مانده اول دوره مشتري ، تامين كننده ، صندوق و حساب بانكي ، موجودي اول دوره كالا و چك هاي استقراري دريافتي و پرداختي به صورت سيستمي ثبت و براي آنها سند افتتاحيه ثبت ميشود. مابقي حساب ها از جمله تنخواه، دارايي ها، تسهيلات و ... را بايد بعد از صدور سند افتتاحيه آن سند را ويرايش كرد و با استفاده از + (افزودن قلم) پايين فرم ثبت آنها را انجام داد. ')), 0, 0, 2256 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1042, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('سيستم حسابداري سپيدار چند سطحي مي باشد؟ ')), Ltrim(RTrim('نرم افزار حسابداري سپيدار همكاران سيستم، شامل چهار سطح (گروه/كل/معين/تفصيل) مي باشد.  ***توجه: در صورت نياز مي توان از ويژگي  پيگيري  حساب معين نيز به عنوان جزئيات حساب تفصيل در سند حسابداري نيز استفاده نمود. ')), 0, 0, 762 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1043, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('نحوه ثبت سند حسابداري ارزي در سيستم چگونه است؟ ')), Ltrim(RTrim('جهت صدور سند ارزي ابتدا بايد به قسمت درختواره حسابها رفته و براي معين هاي موردنظر ويژگي ارزي را فعال نموده، سپس يك سند حسابداري جديد باز كرده و سه ستون ( ارز، نرخ ارز، مبلغ ارز) براي شما فعال مي گردد. (توجه داشته باشيد كه براي ثبت ارزي بايد زير سيستم ارزي را در امكانات قفل خود داشته باشيد)')), 0, 0, 757 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1044, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('هنگام حذف سند حسابداري پيغام  : ( سند صادر شده توسط ساير سيستم ها قابل حذف نيست.) نشان داده ميشود، علت چيست؟ ')), Ltrim(RTrim('در سيستم نمي توان اسنادي را كه توسط ساير سيستم ها (بجز سيستم حسابداري) بصورت خودكار صادر شده اند، از داخل سيستم حسابداري حذف كرد. براي اين كار حتما بايد وارد سيستم مربوطه شده ( مثلا مشتريان و فروش)  و سند حسابداري مربوطه را ( مثلا فاكتور فروش ) از داخل همان فرم حذف نماييد. تنها اسنادي از داخل سيستم حسابداري قابل حذف هستند كه بصورت  دستي يا عمومي صادر شده باشند. ')), 0, 0, 747 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1045, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('هنگام صدور سند حسابداري جديد، در سلكتور مربوطه هيچ حساب معيني نمايش داده نمي شود . علت چيست؟ ')), Ltrim(RTrim('هنگام صدور سند حسابداري به 2 دليل حسابهاي معين قابل نمايش نمي باشند :  1. كدينگ حسابها  ايجاد نشده اند، كه بايد به قسمت درختواره حسابها رفته و از بالاي فرم آرم سپيدار را باز كرده و درج حسابهاي پيش فرض را بزنيد.   2-  در پنجره انتخاب معين ، ستون هاي كد و عنوان معين حذف شده باشند، كه براي بازگرداندن آنها بايد از پايين پنجره بر روي آيكون انتخابگر ستون كليك كرده و گزينه (بازگرداندن تغييرات ستونها به حالت پيش فرض) را انتخاب كنيد. ')), 0, 0, 813 )
