Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1047, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('چگونه مي توان اسناد حسابداري را به صورت متوالي و بدون باز كردن آنها پرينت گرفت؟ ')), Ltrim(RTrim('از منوي حسابداري قسمت فهرست فرم اسناد حسابدار را باز كرده ، از پايين فرم سمت راست دومين آيتم ، انتخاب چند گانه را كليك نماييد، سپس اسناد مورد نظر جهت چاپ را تيك بزنيد. از پايين فرم سلكتور چاپ را باز كرده و فرمت چاپي مورد نظر را انتخاب نماييد. صفحه باز شده پيش نمايش چاپ است. در آخر از بالاي فرم گزينه پرينت را انتخاب نماييد. ')), 0, 0, 925 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1048, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('چرا بعد از تعريف يك حساب معين جديد، معين مورد نظر، در سند حسابداري نمايش داده نمي شود. ')), Ltrim(RTrim('در منوي حسابداري قسمت درختواره حسابها بررسي كنيد كه نوع حساب تعريف شده حتما حساب معين باشد و به اشتباه حساب كل يا گروه تعريف نشده باشد.  در مرحله بعد بررسي كنيد كه حساب معين فعال باشد. در آخر شايان ذكر است در صورتي كه سند حسابداري باز باشد و حساب معين تعريف شود چون امكان بروز كردن صفحه وجود ندارد، حساب معين ايجاد شده نمايش داده نمي شود و بايد سند حسابداري جديد باز كنيد. ')), 0, 0, 858 )
