Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1067, Ltrim(RTrim('Accounting/Voucher')), Ltrim(RTrim('سند حسابداري')), Ltrim(RTrim('چگونه مي توان اسناد را براساس شماره و تاريخ مرتب نمود؟ ')), Ltrim(RTrim('به دو روش مي توان اسناد را براساس شماره و تاريخ مرتب نمود : 1-از منوي عمودي وارد سيستم حسابداري شده و از قسمت فهرست اسناد حسابداري را باز كرده، از بالاي فرم گزينه شماره گذاري مجدد اسناد كه علامت ( % ) را دارد كليك نماييد. 2- در نسخه هاي بالاتر آيتم شماره گذاري مجدد اسناد در منوي عمليات سيستم حسابداري گنجانده شده و از آنجا قابل انتخاب است. توجه داشته باشيم كه پس از شماره گذاري مجدد اسناد، تنها شماره سند است كه تغيير ميكند و شماره ي عطف غيرقابل تغيير مي باشد. (در صورتي كه در قسمت تنظيمات حسابداري گزينه "مرتب سازي شماره عطف در شماره گذاري مجدد اسناد" فعال باشد شماره عطف مانند شماره سند براساس تاريخ مرتب مي شوند)')), 0, 0, 605 )
