Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1085, Ltrim(RTrim('Accounting/CloseAccounts')), Ltrim(RTrim('اصلاح طبقه بندي حسابها')), Ltrim(RTrim('در صورتي كه يك حساب معين چند بار به اشتباه با عنوان هاي متفاوت ايجاد و استفاده شده باشد و در سند افتتاحيه نيز گردش داشته باشد، چگونه ميتوان تبديل به يك معين كرد؟ ')), Ltrim(RTrim('ابتدا مي بايست از ميان كد معين هاي تعريف شده يكي را در نظر بگيريد و گردش ديگر كدهاي معين اشتباه را با اين حساب معين و به يكي از روش هاي ذيل اصلاح كنيد:   الف - در صورتي كه در سال اول استفاده از سيستم مي باشيد و امكان ويرايش سند افتتاحيه صادر شده را داريد:   روش اول - اگر اين حساب معين از آن دسته حسابهايي است كه بعد از صدور سند افتتاحيه به صورت دستي به سند اضافه شده است، در سند افتتاحيه حساب معين اشتباه را به حساب معين جديد و صحيح تغيير دهيد.   روش دوم - اگر اين حساب معين از آن دسته حسابهايي است كه در تنظيمات نرم افزار درج شده و در سند افتتاحيه فراخواني شده است، سند افنناحيه را حذف كرده و پس از اصلاح آن حساب معين در تنظيمات، سند افتتاحيه را مجددا صادر كنيد. در اين حالت سند افتتاحيه صادر شده با حساب معين اصلاح شده صادر خواهد شد.  ب - در صورتي كه در سال دوم به بعد مي باشيد به روش ذيل عمل كنيد:   1 - از سيستم حسابدراي، گزينه  اصلاح طبقه بندي حسابها  را انتخاب كنيد.   2 - در فرم باز شده از بالاي فرم معين مقصد را معين صحيح قرارداده و از پايين فرم معين هاي تكراري را به سمت چپ انتقال دهيد   3 - در انتها پس از ثبت توضيحات لازم فرم را ذخيره كنيد   4 - با اين كار، مانده بدهكار يا بستانكار حساب هاي معين انتخاب شده به معين مقصد انتقال پيدا مي كند كه مي توانيد حساب هاي صفر شده را غير فعال كنيد.')), 0, 0, 1030 )
