Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1109, Ltrim(RTrim('Accounting/Review')), Ltrim(RTrim('مرور حساب‌ها')), Ltrim(RTrim('به چه صورت مي توان از گزارش مرورحسابهاي چند ساله در سيستم استفاده نمود؟ ')), Ltrim(RTrim('جهت گزارش گيري چند ساله (متوالي) بايد به شيوه زير عمل كرد:   1- نسخه برنامه را به بالاترين نسخه موجود ارتقا دهيد.2- در منوي عمودي حسابداري، از قسمت عمليات، بر روي گزينه مرور حسابها كليك كنيد.   3- در فرم باز شده، از بالاي فرم مي توان محدوده تاريخي چند سال مالي را وارد نمود. 4- سپس از پايين فرم آيكون بروزرساني يا رفرش را فشار دهيد.   نكته: با دو بار كليك كردن به روي هر حساب امكان فيلتر كردن و گرفتن گردش آن حساب وجود دارد. ')), 0, 0, 625 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1110, Ltrim(RTrim('Accounting/Review')), Ltrim(RTrim('مرور حساب‌ها')), Ltrim(RTrim('چگونه مي توان از گزارشات سيستم حسابداري سپيدار استفاده نمود؟ ')), Ltrim(RTrim('گزارشات حسابداري سيستم سپيدار از چهار قسمت مرور حسابها، گزارش ترازها، گزارش دفاتر و گزارش ساز قابل تهيه مي باشد كه كاربر با توجه به نياز خود مي تواند از هريك از آن ها استفاده نمايد. گزارش مرور حسابها : در اين قسمت مي توان از حسابها به تفكيك در كليه سطوح ( گروه، كل، معين و تفصيل ) و همچنين بر اساس ارتباط بين آنها گزارش تهيه نمود. به عنوان مثال اگر بخواهيم از مانده حساب دارايي هاي جاري گزارش بگيريم مي توان در قسمت مرور حسابها تب  گروه  مانده اين حساب را مشاهده كرد. يا اگر بخواهيم ببينيم كه گروه دارايي هاي جاري از چه كل هايي تشكيل شده است پس از فيلتر نمودن حساب گروه به تب كل رفته و در اين قسمت كليه حساب كل هايي كه تشكيل دهنده دارايي هاي جاري هستند مشاهده مي كنيم. به همين ترتيب اگر بخواهيم مانده حسابهاي دريافتني را كه يك حساب كل است مشاهده كنيم بر روي تب كل كليك مي كنيم و مانده حسابهاي دريافتني را مشاهده مي كنيم. براي مشاهده اينكه حسابهاي دريافتني از چه معين هايي تشكيل شده است ابتدا با دوبار كليك كردن حساب كل مورد نظر را فيلتر نموده (رنگ آن سبز مي شود) سپس به تب  معين  مي رويم. اگر بخواهيم بدانيم كه حسابهاي دريافتني از چه اشخاصي تشكيل شده است ابتدا حساب معين حسابهاي دريافتني را در تب معين فيلتر نموده سپس بر روي تب  تفصيلي  كليك مي كنيم تا اشخاصي كه از آنها حساب دريافتني داريم به ما نمايش دهد. تب گردش نيز گردش يك حساب را به ما نمايش مي دهد. به عنوان مثال اگر بخواهيم گردش حساب يك شخص (شركت الفا) را مشاهده كنيم، در قسمت مرور حسابها تب تفصيلي حساب تفصيلي(شركت الفا) مورد نظر را فيلتر نموده سپس وارد تب گردش مي شويم. در اين قسمت گردش حساب شخص با كليه حسابها نمايش داده خواهد شد. همچنين مي توان بعد از فيلتر كردن حساب تفصيلي به تب معين رفته و حسابهاي معين مرتبط با آن حساب تفصيلي ر ا مشاهده نمود. قابل ذكر است كه در فرم مرور حسابها امكان گزارش گيري از سطوح بالا به پايين يا از پايين به بالا وجود دارد. گزارش ترازها: در گزارش تر