Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1376, Ltrim(RTrim('ReceiptAndPaymnet/Receipt')), Ltrim(RTrim('رسيد دريافت')), Ltrim(RTrim('چگونه ميتوان از عمليات ثبت شده فاقد سند حسابداري گزارش تهيه كرد؟ ')), Ltrim(RTrim('به دو روش ذيل:   روش اول:   در صورتي كه هدف كاربر تنها مشاهده اين عمليات براي طرف حسابها باشد، مي بايست:   در منوي عمودي، از  مشتريان و فروش  و از قسمت عمليات گزينه  مرور جامع طرف حساب  را انتخاب كنيد.   بدون فيلتر كردن هيچ يك از طرف حسابها، سربرگ  اسناد طرف حساب  را انتخاب كرده از ستون  شماره سند حسابداري  و از ستون  تاريخ  گزينه  خالي باشد  را فيلتر نمائيد.   روش دوم:   در صورتي كه هدف كاربر علاوه بر مشاهده كردن، امكان صدور سند حسابداري آن عمليات نيز باشد، مي بايست از فهرست هر يك از عمليات ثبت شده، در ستون  شماره سند حسابداري  و  تاريخ سند حسابداري  گزينه  خالي  را فيلتر كنيد.   ***توجه: در صورتي كه ستون هاي مذكور وجود نداشت، از پايين صفحه گزينه انتخابگر ستون را انتخاب كرده و از قسمت  انتخابگر ستون  در ليست باز شده مواردي كه لازم مي باشد را دوبار كليك كرده تا به ليست اضافه گردد. ')), 0, 0, 1467 )
