Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1547, Ltrim(RTrim('Asset/Repair')), Ltrim(RTrim('مخارج پس از تحصيل')), Ltrim(RTrim('چگونه ميتوان تعميرات و الحاقيات اقلام دارايي ثابت را ثبت نمود؟ ')), Ltrim(RTrim('از منوي عمودي سيستم  دارايي ثابت  را انتخاب كنيد، سپس از قسمت عمليات گزينه  مخارج پس از تحصيل دارايي  را انتخاب كرده و در صفحه باز شده به ترتيب ذيل عمل نمائيد:   1- از بالاي فرم اطلاعات مربوط به شماره، تاريخ، طرف مقابل و حساب معين را تعيين نمائيد.   2- سپس از پايين صفحه با علامت (+) سطر جديد ايجاد كرده و در سطر ايجاد شده پلاك مورد نظر را انتخاب كنيد و نوع مخارج را يكي از حالت هاي  گسترش و الحاق  يا  تعميرات اساسي  انتخاب نماييد.   3- پس از وارد نمودن مبلغ  بهاي تمام شده  فرم را ذخيره نمائيد.   **نكته1: در صورتي كه مخارج، گسترش و الحاق انتخاب شود وارد كردن ارزش اسقاط اجباري مي باشد در حالي كه اگر تعمير اساسي انتخاب شود ارزش اسقاط برابر 0 و غير فعال مي باشد.   **نكته2: در صورتي كه براي مخارج پس از تحصيل فاكتور دارايي (از نوع خدمت يا دارايي) ثبت شده باشد مي توان در قسمت پايين فرم فاكتور هاي مربوط با آن را انتخاب كرد و بهاي تمام شده از فاكتور هاي انتخاب شده محاسبه شوند. ')), 0, 0, 2907 )
