Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1600, Ltrim(RTrim('General/Backup')), Ltrim(RTrim('تهيه نسخه پشتيباني و بازيابي')), Ltrim(RTrim('آيا امكان تهيه نسخه پشتيبان اتوماتيك وجود دارد؟ چگونه؟ ')), Ltrim(RTrim('بله؛   در منوي عمودي گزينه  تنظيمات  و از قسمت عمليات گزينه  تنظيمات تهيه نسخه پشتيبان خودكار  را انتخاب نمائيد.   در صفحه باز شده به ترتيب ذيل عمل نمائيد:   1- گزينه  فعال سازي تهيه پشتيبان خودكار  را فعال نمائيد.   2- از گزينه  مسير فايل بر روي سرور  مسير قرارگيري بكاپ بر روي سرور را مشخص نمائيد.   3- از قسمت  زمانبندي  با توجه به سياست پشتيبان گيري در مجموعه از بين نوع هاي  روزانه/ساعتي  يك روش پشتيبان گيري را مشخص كرده و  مقدار  بازه زماني تهيه نسخه پشتيبان خودكار را از (تهيه پشتيبان هر XX روز/ساعت) را به جاي كاراكتر هاي XX وارد نموده و ساعت شروع عمليات را نيز تعيين نمائيد.   4- در صورت نياز به رمز نگاري نسخه پشتيبان ميتوان قسمت  فشرده سازي فايل  را فعال كرده رمز مورد نظر را وارد نمود.   **نكته: با توجه به اينكه نسخه هاي پشتيبان حجم زيادي از حافظه سرور را اشغال ميكند، ميتوان در صفحه جاري گزينه  حذف فايلهاي قديمي اگر تعداد آنها بيش از اين تعداد باشد  را فعال كرده و مقدار مورد نظر جهت نگهداري آخرين نسخه پشتيبان را واد نمود. براي مثال:در صورتي كه مقدار وارد شده عدد 8 باشد، در صورتي كه تعداد نسخه هاي پشتيبان به عدد 8 رسيد هنگام تهيه نسخه پشتيبان جديد، قديمي ترين نسخه پشتيبان به صورت خودكار حذف ميگردد. ')), 0, 0, 2866 )
