Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1620, Ltrim(RTrim('General/UserDefinition')), Ltrim(RTrim('كاربر و دسترسي')), Ltrim(RTrim('چگونه ميتوان تنها دسترسي تعدادي انبار را براي كاربر فعال نمود؟ ')), Ltrim(RTrim('ابتدا مي بايست نرم افزار را به آخرين نسخه موجود بروز رساني نموده و به ترتيب ذيل عمل نمائيد:   1- با كاربر Admin وارد سپيدار شده سپس در منوي عمودي وارد سيستم  تامين كنندگان و انبار  شده و از قسمت عمليات گزينه  تنظيمات  را انتخاب نمائيد.   2- در فرم باز شده از سربرگ  تامين كنندگان و انبار  گزينه  اعمال دسترسي ريالي تعدادي انبار  را فعال كرده و بعد از ذخيره تنظيمات، از نرم افزار سپيدار خارج شده و مجدد با كاربر Admin سپيدار را اجرا نمائيد.   3- بعد از ورود مجدد به نرم افزار از منوي عمودي وارد سيستم  تنظيمات  شده و از قسمت فهرست گزينه  كاربران  را انتخاب نمائيد.   4- در صفحه باز شده كاربر مورد نظر را كليك راست كرده  ويرايش  را انتخاب نمائيد.   5- حال از سربرگ  حقوق و دسترسي  از قسمت  تامين كنندگان و انبار  گزينه  دسترسي ريالي انبار  را غير فعال نمائيد. ')), 0, 0, 2894 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1621, Ltrim(RTrim('General/UserDefinition')), Ltrim(RTrim('كاربر و دسترسي')), Ltrim(RTrim('چگونه ميتوان  كاربر جديد  تعريف نمود؟ ')), Ltrim(RTrim('جهت تعريف كاربر جديد با حقوق دسترسي تنظيمات نرم افزار:   1- در منوي عمودي، از  تنظيمات  و از قسمت عمليات گزينه  كاربر جديد  را انتخاب كنيد.   2- در صفحه باز شده اطلاعات  نام و نام خانوادگي  و  نام كاربر  را تكميل نمائيد.   3- در صورت نياز به تعيين رمز هنگام ورود به نرم افزار، از قسمت  كلمه عبور  و  تكرار كلمه عبور  رمز مورد نظر را يادداشت نمائيد.   4- از سربرگ  حقوق دسترسي  ميتوان با توجه به حيطه وظايف كاربر سطح دسترسي كاربر در محيط نرم افزار را تعيين نمود.   5- از سربرگ  تلفن  نيز ميتوان شماره تلفن هاي كاربر را نيز يادداشت نمود. ')), 0, 0, 1591 )
