Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1671, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('درفرم عمليات پايان سال در موقع بستن سيستم تامين كنندگان و انبار خطا ميدهد براي برخي از رسيد انبار،برگشت رسيد انبار وخروج انبار سند حسابداري صادر نشده است راهكار چيست؟ ')), Ltrim(RTrim('براي آنكه بين سيستم حسابداري و ساير سيستم ها مغايرت وجود نداشته باشد بايد براي تمامي فرم هاي سيستم  سند حسابداري صادر شده باشد. اين پيغام از آن جهت نمايش داده ميشود كه براي هر يك از فرم هاي سيستم تامين كنندگان و انبار سند حسابداري صادر نشده باشد در نتيجه بين سيستم حسابداري و سيستم تامين كنندگان و انبار مغايرت خواهيم داشت.راه حل:به سيستم تامين كنندگان و انبار برويد در قسمت فهرست وارد فرم رسيد انبارها و خروج انبارهاو ... شده،  ستون شماره سند حسابداري را روي حالت خالي فيلتر كرده تا فرم هاي بدون سند حسابداري نمايش داده شوند. سپس با استفاده از كليد صدور سند حسابداري در بالاي فرم براي آنها سند حسابداري صادر كنيد.لازم به ذكر است پيغام نمايش داده شده در عمليات پايان سال تنها يك پيام بوده و در صورت تاييد عمليات پايان سال با موفقيت انجام ميشود. ')), 0, 0, 2371 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1672, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('با بستن سيستم مشتريان و فروش چه اسنادي به سال بعد منتقل ميشود؟ ')), Ltrim(RTrim('بعد از بستن سيستم مشتريان فروش در عمليات پايان سال، تنها مانده حساب مشتريان به سال بعد منتقل ميشود.  توجه داشته باشيد، اسناد فروش اعم از فاكتور فروش به سال بعد منتقل نميشوند. ')), 0, 0, 2311 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1673, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('چگونه ميتوان سيستم دريافت و پرداخت را باز كرد؟ ')), Ltrim(RTrim('از پايين صفحه برنامه، سلكتور سال مالي را باز كرده و سال مورد نظر را انتخاب كنيد.  سپس از منوي شركت، قسمت عمليات، فرم عمليات پايان سال را باز كرده، تيك دريافت و پرداخت را فعال كرده و از بالاي فرم با استفاده از علامت قفل باز،  برگشت از بستن  اقدام به باز كردن سيستم دريافت و پرداخت نماييد.  پيش از باز كردن سيستم دريافت و پرداخت به نكات زير توجه داشته باشيد:  1- در صورتي كه در سال مالي بعد از سال مورد نظر، سيستم دريافت و پرداخت بسته باشد، امكان برگشت از بستن وجود ندارد؛  2- در صورتي كه براي چك هاي منتقل شده به سال بعد، عملياتي ثبت شده است، مي بايستي عمليات انجام شده حذف شود. به طور مثال در صورتي چك دريافتني، با وضعيت واگذار به بانك به سال بعد منتقل شده است و در آن سال وصول شده است، بايد عمليات وصول حذف شده تا امكان باز كردن سيستم دريافت و پرداخت در سال بعد فراهم شود.  3- قبل از باز كردن سيستم دريافت و پرداخت، به ترتيب سيستم تامين كنندگان و انبار و مشتريان و فروش را باز كنيد. ')), 0, 0, 2328 )
