Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1677, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('ميخواهم در سال مالي قبل سيستم تامين كنندگان و انبار و برخي از انبارها را باز كنم. اما سيستم خطا ميدهد موجودي انبار نمي تواند منفي باشد. چگونه مي توان اين خطا را برطرف كرد؟ ')), Ltrim(RTrim('بعد از بستن سال مالي مانده كالاها به سال مالي بعد منتقل ميشود و براي آن رسيد انباري از نوع اول دوره ثبت ميشود تا موجودي انبار بدهكار شود. با باز كردن سال مالي اين رسيد حذف ميشود. پس در صورتي كه گردش كالاهاي منتقل شده به سال بعد اين رسيد انبار به گونه اي باشد كه با حذف رسيد انبار منفي شود سيستم مانع از باز كردن انبار ميشود. براي رفع اين خطا ميتوانيد از منوي تنظيمات فرم تنظيمات را باز كرده، در تب تامين كنندگان و انبار تيك كنترل موجودي منفي را برداريد. توجه داشته باشيد با اين كار موجودي منفي در هيچ يك از انبارها كنترل نمي شود. ')), 0, 0, 1062 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1678, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('سيستم مشتريان و فروش را بسته ام. چگونه ميتوانم اين سيستم را مجدد باز كنم؟  ')), Ltrim(RTrim('براي باز كردن سيستم فروش وارد منوي شركت شده و از قسمت عمليات فرم عمليات پايان سال را انتخاب كنيد. سپس مشتريان و فروش را تيك زده و از بالاي فرم كليد قفل باز را انتخاب ميكنيم.   توجه داشته باشيد در صورتي كه از سيستم تامين كنندگان و انبار نيز استفاده ميكنيد ابتدا بايد اين سيستم را باز كنيد. ')), 0, 0, 920 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1679, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('ميخواهم در عمليات پايان سال سيستم دريافت و پرداخت را ببندم ، خطا ميدهد براي برخي از اعلاميه پرداخت ها و رسيد دريافت ها سند حسابداري صادر نشده است.راه حل چيست؟ ')), Ltrim(RTrim('براي آنكه سيستم حسابداري و ساير سيستم ها مغايرت نداشته باشند پس بايد براي تمامي فرم ها سند حسابداري صادر شود. اين پيغام از آن جهت نمايش داده ميشود كه در صورتي كه براي اين فرم سند صادر نشود در نتيجه سيستم حسابداري با سيستم دريافت و پرداخت مغايرت خواهد داشت.  براي رفع اين پيغام ميتوان در سيستم دريافت و پرداخت ، قسمت فهرست وارد فرم رسيدهاي دريافت و يا اعلاميه پرداخت و ... شده،  ستون شماره سند حسابداري را روي حالت خالي فيلتر كنيم تا فرم هاي بدون سند حسابداري نمايش داده شوند. سپس با استفاده از كليد صدور سند حسابداري در بالاي فرم براي آنها سند حسابداري صادر كرد.  لازم به ذكر است پيغام نمايش داده شده در عمليات پايان سال تنها يك پيام بوده و در صورت تاييد عمليات پايان سال با موفقيت انجام ميشود. ')), 0, 0, 968 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1680, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('در زمان بستن انبار سيستم پيغام مي دهد براي برخي از خروج انبارها فاكتور فروش صادر نشده است. راه حل چيست؟')), Ltrim(RTrim('از آنجايي كه شما براي كالاها خروج انبار از نوع فروش صادر نموده ايد اما براي آن ها فاكتور فروش صادر نكرده ايد، مانده مشتري شما در سيستم مشتريان و فروش درست نمي باشد. بنابراين حتما بايد قبل از بستن سيستم انبار و فروش، براي كليه ي خروج انبارها فاكتور فروش صادر كنيد.. جهت گزارش گيري از خروج انبارهايي كه براي آنها فاكتور فروش صادر نشده است مي توانيد به شركت - فعاليت هاي روز رفته و در تب خروج انبارهاي بدون فاكتور آن ها را مشاهده كنيد و اقدام به صدور فاكتور فروش براي آن ها نماييد.')), 0, 0, 254 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1681, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('به دليل اينكه موجودي كالا را در سال قبل اشتباه وارد كرده ايم، در سال جديد نيز با موجودي اشتباه منتقل شده است. چگونه مي توان آن را اصلاح كرد؟ ')), Ltrim(RTrim('ز قسمت تنظيمات تب تامين كنندگان و انباررا باز كرده، سال استقرار سيستم را به سال مالي جاري تغيير دهيد و ذخيره نماييد.   حال مي توانيد رسيد انبار از نوع موجودي ابتداي دوره ثبت كرده و موجودي اوليه را به صورت صحيح وارد انبار نماييد. ')), 0, 0, 569 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1682, Ltrim(RTrim('General/Closing')), Ltrim(RTrim('عمليات پايان سال')), Ltrim(RTrim('چگونه عمليات پايان سال را انجام دهم؟ ')), Ltrim(RTrim('براي بستن سيستم حسابداري بايد مراحل زير را طي نمود : 1- بستن حسابهاي سود و زياني 2- تبديل اسناد موقت به دايم 3- صدور سند اختتاميه در سال جاري و صدور سند افتتاحيه در سال مالي بعد. 4- در صورت لزوم صدور سند كل جهت دفترنويسي كه مي تواند قبل از صدور سند اختتاميه يا بعد از آن باشد.  سپس از منوي شركت، قسمت عمليات، فرم عمليات پايان سال را باز كرده، سيستم ها را انتخاب كرده و از بالاي فرم علامت قفل بسته را انتخاب كنيد.  نكته :  1-    پيش از بستن سيستم ها در صورت لزوم براي اسناد ثبت شده در ساير سيستم ها،  اقدام به صدور سند حسابداري نماييد.   2-    پيش از بستن سيستم مشتريان و فروش:   i.    براي تمامي فاكتورهاي فروش، خروج انبار ثبت نماييد؛   ii.    براي تمامي فاكتورهاي برگشتي، برگشت خروج انبار ثبت نماييد؛   iii.    در صورتي كه در تنظيمات سيستم، گزينه  موجودي قابل فروش با احتساب پيش فاكتور  را فعال كرده ايد، وضعيت پيش فاكتورهاي باز را به خاتمه يافته تغيير دهيد.   3-    پيش از بستن سيستم تامين كنندگان و انبار:   i.    براي تمامي خروج انبارهاي از نوع فروش، فاكتور فروش ثبت نماييد؛   ii.    براي تمامي برگشت خروج انبارهاي از نوع فروش، فاكتور برگشتي ثبت نماييد؛   iii.    براي تمامي انبارها، قيمت گذاري اسناد انبار انجام دهيد.     ')), 0, 0, 783 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1687, Ltrim(RTrim('Accounting/Opening')), Ltrim(RTrim('عمليات اول دوره')), Ltrim(RTrim('چگونه ميتوان مانده اول دوره تنخواه گردان ريالي را در سيستم ثبت كرد؟ آيا مانده اول دوره بايد به صورت سيستمي ثبت گردد؟ ')), Ltrim(RTrim('مانده اول دوره مشتري ، تامين كننده ، صندوق و حساب بانكي ، موجودي اول دوره كالا و چك هاي استقراري دريافتي و پرداختي به صورت سيستمي ثبت و براي آنها سند افتتاحيه ثبت ميشود. مابقي حساب ها از جمله تنخواه، دارايي ها، تسهيلات و ... بايد ثبت دستي شوند. ')), 0, 0, 2256 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1688, Ltrim(RTrim('Accounting/Opening')), Ltrim(RTrim('عمليات اول دوره')), Ltrim(RTrim('حساب معيني براي افتتاحيه درعمليات اول دوره نمايش داده نميشود، علت چيست؟ ')), Ltrim(RTrim('دو دليل مي تواند داشته باشد:  حساب معين مورد نظر در سيستم تعريف نشده است.   حساب معين تعريف شده اما تفصيل پذير است ')), 0, 0, 919 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1689, Ltrim(RTrim('Accounting/Opening')), Ltrim(RTrim('عمليات اول دوره')), Ltrim(RTrim('چگونه ميتوان سند اول دوره اي را كه توسط سيستم صادر شده است، ويرايش نمود؟ ')), Ltrim(RTrim('براي ويرايش سند اول دوره در اولين سال مالي ابتدا مي بايست از قسمت شركت  آيتم  عمليات اول دوره را باز كرده، سپس از بالاي صفحه آيكون حذف سند افتتاحيه را انتخاب كنيم. جهت ويرايش هريك از موارد (موجودي كالا، موجودي بانك، موجودي صندوق، چكهاي استقرار، مانده اول دوره مشتريان يا تامين كنندگان) كافيست از همان بخشي كه اين اطلاعات را ثبت كرده ايم وارد شويم و آن ها را ويرايش نماييم. براي مثال براي ويرايش موجودي اول دوره مشتريان به قسمت شركت- طرف حسابها رفته و با كليك راست بر روي طرف حساب موردنظر و انتخاب گزينه ويرايش، مانده اول دوره مشتري را تغيير دهيم. يا براي ويرايش موجودي اول دوره كالاها از سيستم تامين كنندگان و انبار-  منوي فهرست رسيد انبارها را باز كرده و رسيد انبار مورد نظر را ويرايش مي كنيم. در پايان پس از اعمال تغييرات موردنياز مجددا به قسمت شركت- عمليات اول دوره رفته و پس از تاييد  همه ي موارد از بالاي صفحه بر روي آيكون صدور سند افتتاحيه كليك مي كنيم.( قابل ذكر است اگر بخواهيم مواردي بجز موارد فوق را ويرايش كنيم، كافيست از قسمت عمليات اول دوره از بالاي پنجره آيكون نمايش سند افتتاحيه را فشار داده و موارد ديگر را بصورت دستي به سند اضافه كنيم. ) همچنين در صورتيكه سند افتتاحيه مربوط به اولين سال مالي نباشد و در سالهاي بعد بصورت خودكار صادر شده باشد، امكان هيچ گونه ويرايشي در آن وجود ندارد. اما مي توان از سند افتتاحيه تكثير گرفته و تغييرات لازم را بر روي سند تكثير شده اعمال نمود، سپس سندي كه بصورت خودكار توسط سيستم صادر شده بود را حذف نمود.  ')), 0, 0, 812 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1690, Ltrim(RTrim('Accounting/Opening')), Ltrim(RTrim('عمليات اول دوره')), Ltrim(RTrim('نحوه صدور سند افتتاحيه در اولين سال مالي به چه صورت است؟ ')), Ltrim(RTrim('ابتدا بايد موجودي اول دوره كالا ها را از طريق رسيد انبار از نوع موجودي ابتداي دوره ، مانده حسابهاي مشتريان و تامين كننده ها را از طريق فرم تعريف طرف حساب در فيلد مانده اول دوره، مانده حساب بانكي و صندوق از طريق فرم هاي تعريف هر يك در فيلد مانده اول دوره تكميل نمود. سپس در قسمت شركت ، فرم عمليات اول دوره را باز كرده، براي بررسي همه موارد، روي صفحه راست كليك كرده و موارد وارد شده را تاييد نماييد. پس از وارد كردن حساب معين مورد نظر در قسمت معين افتتاحيه، تاريخ و توضيحات، فرم را ذخيره كرده و از بالاي فرم گزينه صدور سند افتتاحيه را انتخاب نمائيد. در صورتي كه نياز باشد حسابي به سند افتتاحيه اضافه شود از طريق گزينه ويرايش سند افتتاحيه تغييرات لازم را اعمال نماييد. ')), 0, 0, 988 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1691, Ltrim(RTrim('Accounting/Opening')), Ltrim(RTrim('عمليات اول دوره')), Ltrim(RTrim('چگونه ميتوان مانده حساب اول دوره طرف حسابها را ثبت نمود؟ ')), Ltrim(RTrim('در منوي عمودي، از  شركت  و از قسمت فهرست گزينه  طرف حساب ها  را انتخاب كنيد.   در صفحه باز شده، طرف حساب مورد نظر را جستجو كرده و كليك راست كرده گزينه  ويرايش را انتخاب نمائيد.   در صفحه باز شده ميتوان نقش طرف حساب (مشتري/تامين كننده/واسط) را تعيين كرده و در صورت وجود مانده اول دوره در نقش انتخاب شده، مبلغ اول دوره هر يك را با مشخص كردن ماهيت مبلغ مانده وارد نمائيد.   با اين عمل، اطلاعات مانده اول دوره وارد شده در عمليات اول دوره جهت صدور سند افتتاحيه قابل رؤيت خواهد بود.   **نكته: در صورتي كه سيستم به نسخه 5.1.2 و بالاتر بروز باشد مي توان مانده اول دوره  واسط  را وارد نمود.   ***توجه: سال استقرار طرف حساب با نقش واسط قابل انتخاب نمي باشد، لذا اولين سال باز سيستم مشتريان و فروش، به عنوان سال استقرار واسط تلقي مي گردد.   ***توجه: در صورتي هنگام وارد كردن مانده اول دوره واسط، اولين سال مالي سيستم نباشد، مي توان در تعريف طرف حسابها مانده واسط را وارد كرده و جهت نمايش در سيستم حسابدراي، سند حسابداري جديد صادر نمود. ')), 0, 0, 1137 )
