Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1799, Ltrim(RTrim('General/Party')), Ltrim(RTrim('طرف حساب')), Ltrim(RTrim('در سيستم براي پرسنل طرف حساب تعريف كرده ام ولي در سيستم حقوق و دستمزد مشاهده نمي شود. علت چيست؟ ')), Ltrim(RTrim('از منوي شركت ، قسمت فهرست، فرم طرف حساب را باز كنيد. طرف حساب مورد نظر را انتخاب كرده و كليك راست كرده گزينه ويرايش را انتخاب كنيد. سپس در تب مشخصات كارمند، تيك كارمند را فعال كنيد. ')), 0, 0, 1262 )
