Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1883, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان هزينه هاي حمل كالا ها را به مبلغ بهاي تمام شده كالا اضافه كرد؟ ')), Ltrim(RTrim('در صورتي كه در خريد انجام شده علاوه بر كالاها، هزينه اي مانند هزينه حمل، بسته بندي، ترخيص و ... اضافه شده است، هنگام ثبت ميبايست آن را بين اقلام مختلف تقسيم كرده و براي هر قلم، سهمي از آن به عنوان هزينه حمل در نظر بگيريد. دقت داشته باشيد كه هزينه حمل كالا ممكن است به دو حالت اعمال شود:   حالت اول:   هزينه هاي جانبي در پايين فاكتوري كه براي خريد دريافت شده است ، اضافه شده است . در اين حالت نبايد از اطلاعات حمل استفاده كرد و بايد در قيمتي كه براي كالا در رسيد انبار وارد مي شود اين مبالغ نيز در نظر گرفته شود. در اين حالت هزينه هاي جانبي نيز به حساب بستانكاري تامين كننده اضافه ميگردد .   براي مثال مبلغ كل خريد كالاها 1/000/000 ريال و هزينه حمل 100/000 ريال جمع 1/100/000 ريال   در اين روش سند حسابداري به صورت ذيل صادر خواهد شد:  موجودي كالا(مبلغ كالا + هزينه حمل) 1/100/000  (بدهكار)                                                حسابهاي پرداختني 1/100/000  (بستانكار)   حالت دوم:   هزينه هاي جانبي توسط شخص ثالثي طي بارنامه ها يا اسنادي جدا از فاكتور خريد كالا مانند مؤسسات حمل و نقل كالا صورت گرفته باشد. در اين حالت بايد مبلغ حمل را در اين قسمت وارد كنيد. در اين حالت هزينه هاي جانبي به حساب بستانكاري تامين كننده كالا درج نخواهند شد و مي بايست هزينه حمل را به صورت جداگانه در سيستم ثبت نمود   براي مثال مبلغ كل خريد كالاها 1/000/000 ريال و هزينه حمل 100/000 ريال جمع 1/100/000 ريال   در اين حالت هزينه حمل به ترتيب ذيل در سيستم ثبت مي گردد:   الف - بعد از انتخاب اقلام كالا، از بالاي صفحه گزينه  اطلاعات حمل  انتخاب شود.   ب - در صفحه باز شده بعد از وارد كردن اطلاعات مورد نظر مي توان مبلغ حمل به يكي از روش هاي (مقداري، مبلغي، به نسبت مساوي) بر روي كالا ها تسهيم گردد.   در اين روش سند حسابداري به صورت ذيل صادر خواهد شد:  موجودي كالا 1/100/000  (بدهكار)                                               حسابهاي پرداختني  1/000/000                                               واسط كرايه حمل موجوديها 100/000 (بستانكار)  **نكته: بايد توجه داشت كه حساب هزينه در اين سند بستانكار نمي شود و حساب كرايه حملي كه در سيستم در نظر گرفته شده است (يك حساب واسط است) بستانكار شده و حساب فوق در زمان ثبت پرداخت هزينه حمل (اعلاميه پرداخت) بدهكار شده و در مجموع حساب صفر خواهد شد. ')), 0, 0, 260 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1884, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان تنظيم كرد هنگام خريد يا فروش كالا مبلغ  ماليات و عوارض ارزش افزوده  به صورت اتوماتيك محاسبه نشود؟ ')), Ltrim(RTrim('راه حل اول:   در صورتي كه بخواهيد هنگام خريد يا فروش كالا مبلغ ماليات و عوارض كلاً محاسبه نشود بايد در هنگام تعريف كالاي مورد نظر، تيك گزينه(هاي)  معاف از ماليات خريد/فروش  را براي كالا فعال كنيد.   راه حل دوم:   در صورتي كه بخواهيد فقط در يك فاكتور خاص براي آن كالا ماليات محاسبه نشود، مبلغ ماليات محاسبه شده براي كالا در فاكتور را به صورت دستي صفر نماييد. ')), 0, 0, 255 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1885, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('به چند روش مي توان مبلغ حمل در فرم رسيد انبار را در   في تمام شده  كالا ها تسهيم كرد؟ ')), Ltrim(RTrim('اطلاعات حمل را مي توان به سه روش در فرم رسيد انبار تسهيم كرد:   الف - به نسبت مساوي   ب - مقداري   ج - مبلغي ')), 0, 0, 354 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1886, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('آيا امكان صدور رسيد انبار با نوع  اماني  وجود دارد؟ چگونه؟ ')), Ltrim(RTrim('بله؛   ابتدا، در فرم رسيد انبار تيك گزينه  ساير  را فعال كرده، سپس انبار مورد نظر را مشخص و تحويل دهنده را نيز از انواع تفصيلي (به طور مثال مركز هزينه، ساير، طرف مقابل و ...) انتخاب كرده و حساب معين را  كالاي اماني ديگران نزد ما  قرار دهيد.   در انتها با گزينه  مثبت  پايين صفحه سطر جديد ايجاد كرده و كالا ها را با في حداقل 10 ريال وارد كرده و فرم را ذخيره نمائيد. ')), 0, 0, 356 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1887, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان رسيد انبار از نوع  اول دوره  را  ويرايش  يا  حذف  كرد؟ ')), Ltrim(RTrim('رسيد انبار اول دوره در صورتي قابل حذف كردن يا ويرايش كردن مي باشد كه موارد ذيل رعايت شده باشد:   در صورتي كه بعد از ثبت رسيد انبار اول دوره، سند افتتاحيه سيستم را از قسمت عمليات اول دوره صادر شده است، مي بايست ابتدا از قسمت  شركت  از گزينه  عمليات اول دوره ، سند افتتاحيه را حذف كرده، سپس رسيد انبار(هاي) اول دوره را ويرايش يا حذف كرد.   **نكته: در صورتي كه رسيد انبار اول دوره مربوط به سال مالي دوم يا بعداز آن در سپيدار باشد جهت حذف آن مي بايست عمليات پايان سال انجام شده در سال قبل را باز كرد.   **نكته: در صورتي كه لازم باشد رسيد انبار اول دوره در سال دوم يا بعداز آن را دستي ويرايش كرد، مي بايست سال استقرار سيستم تامين كنندگان و انبار را از تنظيمات تغيير داد. ')), 0, 0, 357 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1888, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('انواع رسيد انبار را توضيح دهيد؟ ')), Ltrim(RTrim('در سيستم سپيدار همكاران سيستم، پنج نوع رسيد انبار وجود دارد كه باعث افزايش موجودي در انبار مي گردد:   1- خريد داخلي: جهت خريد هاي انجام شده از داخل كشور مورد استفاده قرار مي گيرد. در اين نوع رسيد پس از وارد كردن مشخصات انبار، تحويل دهنده،فاكتور خريد و در صورت لزوم اطلاعات حمل، ميتوان مقادير مربوط به ماليات بر ارزش افزوده هر قلم را نيز به طور جداگانه وارد كرد.   2- خريد خارجي: جهت خريد هاي انجام شده از خارج كشور مورد استفاده قرار مي گيرد. در اين نوع رسيد پس از وارد كردن مشخصات انبار و تحويل دهنده و در صورت لزوم اطلاعات حمل، اطلاعات كالا ها را وارد مي توان وارد كرد. تفاوت اين نوع رسيد با رسيداز نوع داخلي اين مي باشد كه مبلغ ماليات بر ارزش افزوده به اقلام درج شده در آن تعلق نمي گيرد.   **توجه: در رسيد هاي انبار از نوع خريد (وارداتي) امكان صدور رسيد انبار بر مبناي فاكتور خريد وجود ندارد.   **نكته: در صورت انتخاب رسيد انبار از نوع (داخلي يا وارداتي) در قسمت تحويل دهنده تنها تفصيل هاي از نوع طرف مقابل با نقش تامين كننده را نمايش خواهد داد.   3- توليد: در صورتي كه محصولي را توليد كرده باشيم، جهت رسيد كردن محصولات  توليد شده به انبار مي بايست نوع رسيد انبار را توليد قرار داد. خاصيت رسيد توليد اين است كه مي توان در آن لحظه فقط مقدار را وارد كرده و مبلغ را در فرصتي ديگر و پس از بدست آمدن بهاي تمام شده محصول بصورت دستي وارد نمود.   **نكته: در صورت انتخاب رسيد انبار از نوع توليد در قسمت تحويل دهنده تنها تفصيل هاي از نوع مركز هزينه را نمايش خواهد داد.   4- ساير: جهت رسيد كردن كالاهايي كه از نوع موارد ذكر شده بالا نباشند، بكار ميرود. مانند ثبت كالاي اماني ،كسر و اضافات و ...    5- موجودي ابتداي دوره: در صورتي كه لازم باشد كالا هاي موجود از سال قبل را به عنوان موجودي اول دوره انبار در سيستم تامين كنندگان و انبار وارد شود، مي توان رسيد انبار از نوع موجودي ابتداي دوره را صادر كرد. ')), 0, 0, 358 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1889, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگون مي توان بر مبناي فاكتور خريد، رسيد انبار صادر كرد؟ ')), Ltrim(RTrim('پس از ثبت فاكتور خريد، در صورتي كه لازم باشد بر مبناي فاكتور خريد به مقدار موجودي كالا ها در انبار اضافه كرد مي بايست به ترتيب ذيل عمل كرد:   1- ابتدا در فرم رسيد انبار نوع رسيد انبار را  خريد داخلي  انتخاب كرده و مشخصات انبار و تحويل دهنده را مشخص كنيد.   2- در قسمت فاكتور خريد ليست همه فاكتورهاي خريد كه حداقل يك قلم دارند كه براي همه مقادير خريداري شده آن رسيد انبار صادر نشده است و همچنين انبار انتخاب شده در بالاي فرم رسيد انبار يكي از انبارهاي مرتبط با آن كالا باشد نمايش داده مي شود.   **نكته: در صورتيكه قبل از انتخاب  فاكتور خريد ، تنها انبار انتخاب شد باشد، همه فاكتورهاي خريدي كه اقلام داخل آن در ارتباط با انبار باشد را نمايش داده خواهد؛ اما در صورتيكه قبل از انتخاب  فاكتور خريد ، علاوه بر انبار  تحويل دهنده  نيز انتخاب شده باشد، تنها فاكتورهاي خريدي كه  تامين كننده  آنها همان  تحويل دهنده  انتخاب شده باشد نمايش داده ميشود.   3- پس از انتخاب فاكتور خريد، تمام اقلام داخل فاكتور خريد كه به انبار انتخاب شده در بالاي فرم ارتباط دارند فراخواني خواهد شد   4- في و مبلغ اقلام منتقل شده از فاكتور خريد به رسيد انبار بصورت زير تعيين ميشود :  مبلغ  كل  هر قلم = مبلغ  كل  آن قلم در فاكتور خريد - مبلغ  تخفيف  آن قلم در فاكتور خريد  مبلغ  في  هر قلم = مبلغ  كل  آن قلم در رسيد انبار /  تعداد اصلي  آن قلم در رسيد انبار  **نكته: دقت داشته باشيد كه همه اطلاعات مبلغي مندرج در رسيد انبار، به ارز پايه سيستم خواهد بود. درصورتيكه اطلاعات فاكتور خريدي كه انتخاب ميكنيد به ارزي به جز ارز پايه باشد، اطلاعات ارزي هر قلم در قسمت  ارز  زير صفحه درج شده و قابل مشاهده خواهد بود. ')), 0, 0, 359 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1890, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان رسيد انبار از نوع  توليد  صادر كرد؟ ')), Ltrim(RTrim('ابتدا، در فرم رسيد انبار تيك گزينه  توليد  را فعال كرده، سپس انبار مورد نظر را مشخص كرده و در قسمت تحويل دهنده مركز هزينه (خط توليد) مورد نظر را انتخاب كنيد.   در انتها با گزينه  مثبت  پايين صفحه سطر جديد ايجاد كرده و كالا ها را با في بهاي تمام شده وارد كرده و فرم را ذخيره نمائيد.   **نكته: در صورتي كه امكان محاسبه مبلغ بهاي تمام شده كالا هاي توليد شده وجود نداشته باشد، مي توان تنها تعداد اقلام را وارد كرده و فرم را ذخيره نمود. ')), 0, 0, 360 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1891, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('موجودي انبار در سيستم  حسابداري  و سيستم  تامين كنندگان و انبار  يكسان نيست، علت چيست؟ ')), Ltrim(RTrim('وجود مغايرت بين سيستم هاي انبار و حسابداري ميتواند ناشي از موارد زير باشد:   1- بدون صدور رسيد انبار، برگشت رسيد انبار، خروج انبار، برگشت خروج انبار در سيستم حسابداري سند حسابداري دستي صادر شده باشد.   2- براي تعدادي از رسيد هاي انبار، برگشت رسيد انبارها،خروج انبارها و يا برگشت خروج انبارها سند حسابداري صادر نشده باشد.   3- سند افتتاحيه صادر نشده باشد. ')), 0, 0, 362 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1892, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('نحوه ثبت تخفيف در رسيد انبار به چه صورت مي باشد؟ ')), Ltrim(RTrim('امكان ثبت تخفيف در رسيد انبار وجود ندارد، جهت ثبت تخفيف ميتوان به دو روش عمل كرد:   1- در صورتي كه لازم باشد مبلغ تخفيف از بهاي تمام شده كالا ها در رسيد انبار كسر گردد، مي بايست ابتدا اطلاعات خريد كالا ها را به همراه تخفيف در فاكتور خريد ثبت كرد، سپس رسيد انبار كالاها بر مبناي فاكتور خريد صادر شود تا مبلغ تخفيف در بهاي تمام شده كالا ها نيز اعمال گردد.   2- در صورتي كه مبلغ تخفيف بعد از رسيد انبار حاصل شده و به بهاي تمام شده كالا ها تاثيري نداشته باشد، مي توان هنگام پرداخت مبلغ بستانكاري به تامين كننده به عنوان تخفيف در فرم اعلاميه پرداخت در نظر گرفته شود، همچنين مي توان مبلغ تخفيف را از اعلاميه بدهكار/بستانكار ثبت كرده و از حساب تامين كننده كسر نمود. ')), 0, 0, 363 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1893, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('آيا ميتوان اقلام از نوع خدمت را در رسيد انبار ثبت كرد؟ ')), Ltrim(RTrim('خير؛ امكان ثبت قلم از نوع خدمت در رسيد انبار وجود ندارد.   1- در صورتي كه خريد خدمت در رابطه با كالاي خريداري شده باشد و لازم باشد كه مبلغ آن به بهاي تمام شده كالا اضافه گردد مي بايست مبلغ آنرا به يكي از دو روش ذيل به بهاي تمام شده كالا اضافه كرد:   الف - در صورت امكان زماني كه تعداد اقلام كالا در رسيد انبار يك قلم باشد مبلغ خدمات به صورت دستي به مبلغ كل كالا اضافه گردد.   ب - در صورتي كه تعداد اقلام كالا ها در رسيد زياد باشد، مي بايست مبلغ خدمات را از قسمت اطلاعات حمل به يكي از سه روش (مقداري، مبلغي، به نسبت مساوي) بر روي اقلام كالا ها سر شكن كرد، با اين عمل معين  واسط كرايه حمل موجودي ها  بستانكار ميگردد، كه ميتوان هنگام ثبت اعلاميه پرداخت آنرا بدهكار كرده و در نهايت آنرا صفر كرد.   2- در صورتي كه خدمات خريداري شده به عنوان هزينه براي شركت باشد، مي بايست به عنوان خريد خدمت از فرم  فاكتور خريد خدمات  ثبت گردد. ')), 0, 0, 364 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1894, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان عمليات از قبل ثبت شده را تكثير (كپي) كرد؟ ')), Ltrim(RTrim('در منوي عمودي، از سيستم مورد نظر و از قسمت  فهرست  آيتم مربوط به عمليات ثبت شده را انتخاب كنيد.   در صفحه باز شده، بر روي هر يك از عملياتي كه قصد تكثير(كپي) كردن آنرا داريد، كليك راست كرده و گزينه  تكثير  را انتخاب كرده و سپس فرم را ذخيره نمائيد. ')), 0, 0, 314 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1895, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('هنگام صدور سند حسابداري براي (خروج انبار، برگشت خروج انبار، برگشت رسيد انبار بدون مبنا و رسيد هاي انبار از نوع انتقال بين انبار) پيغام    سند حسابداري براي عمليات شماره A صادر نشد. سند انبار بايد قبل از صدور سند حسابداري قيمت گذاري شود. لطفا بعد از انجام عمليات قيمت گذاري، اقدام به صدور سند حسابداري نماييد.    نمايش داده مي شود، علت چيست؟ ')), Ltrim(RTrim('جهت صدور سند حسابداري براي عمليات (خروج انبار، برگشت خروج انبار، برگشت رسيد انبار بدون مبنا و رسيد هاي انبار از نوع انتقال بين انبار) حتما بايد عمليات مذكور داراي قيمت باشند، جهت قيمت دار كردن آن ها نيز بايد تا تاريخ آخرين خروج انبار ثبت شده قيمت گذاري اسناد انبار صورت گرفته باشد. ')), 0, 0, 331 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1896, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('هنگام صدور رسيد انبار بر مبناي فاكتور خريد، در سلكتور مربوط به  فاكتورخريد  شماره فاكتور خريد مشاهده نميشود، علت چيست؟ ')), Ltrim(RTrim('عدم نمايش فاكتور خريد در فرم رسيد انبار به دلايل ذيل مي باشد:   1- كالا هاي انتخاب شده در فاكتور خريد به انبار انتخاب شده در رسيد انبار ارتباط نداشته باشد.   2- تاريخ رسيد انبار از تاريخ فاكتور خريد كوچكتر مي باشد.   3- طرف مقابل  تحويل دهنده  در فاكتور خريد و رسيد انبار يكسان نيست.   4- بر مبناي اقلام فاكتور خريد قبلا رسيد انبار صادر شده باشد.   **نكته:در صورتي كه نوع رسيد انبار از نوع  خريد(وارداتي)  انتخاب شده باشد، آيتم فاكتور خريد غير فعال خواهد بود. ')), 0, 0, 347 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1897, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('كاربرد گزينه  اطلاعات حمل  در فرم رسيد انبار چيست؟ ')), Ltrim(RTrim('در صورتي كه خريد انجام شده شامل هزينه باشد و هزينه هاي جانبي توسط شخص ثالثي طي بارنامه ها يا اسنادي جدا از فاكتور خريد كالا مانند مؤسسات حمل و نقل كالا صورت گرفته باشد مي بايست مبلغ حمل را در اين قسمت وارد كنيد. در اين حالت هزينه هاي جانبي به حساب بستانكاري تامين كننده كالا درج نخواهند شد و مي بايست هزينه حمل را به صورت جداگانه در سيستم ثبت نمود   براي مثال مبلغ كل خريد كالاها 6/000/000 ريال و هزينه حمل 100/000 ريال جمع 6/100/000 ريال   در اين حالت هزينه حمل به ترتيب ذيل در سيستم ثبت مي گردد:   الف - بعد از انتخاب اقلام كالا، از بالاي صفحه گزينه  اطلاعات حمل  انتخاب شود.   ب - در صفحه باز شده بعد از وارد كردن اطلاعات مورد نظر مي توان مبلغ حمل به يكي از روش هاي (مقداري، مبلغي، به نسبت مساوي) بر روي كالا ها تسهيم گردد.   در اين روش سند حسابداري به صورت ذيل صادر خواهد شد:  موجودي كالا 6/100/000  (بدهكار)                                               حسابهاي پرداختني  1/000/000                                               واسط كرايه حمل موجوديها 600/000 (بستانكار)   **نكته: بايد توجه داشت كه حساب هزينه در اين سند بستانكار نمي شود و حساب كرايه حملي كه در سيستم در نظر گرفته شده است (يك حساب واسط است) بستانكار شده و حساب فوق در زمان ثبت پرداخت هزينه حمل (اعلاميه پرداخت) بدهكار شده و در مجموع حساب صفر خواهد شد. ')), 0, 0, 348 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1898, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان رسيد انبار را  ويرايش  نمود؟ ')), Ltrim(RTrim('در صورتي ميتوان  ويرايش رسيد انبار  را انجام داد كه شرايط زير رعايت شده باشد:   1- سند حسابداري رسيد انبار حذف شده باشد.   2- اعلاميه پرداخت رسيد انبار حذف شده باشد.   3- در صورتي كه بر مبناي رسيد انبار مورد نظر، سند برگشت رسيد انبار صادر شده باشد، مي بايست حذف گردد.   4- در صورتي كه در بازه تاريخ رسيد انبار، عمليات قيمت گذاري اسناد انبار انجام شده باشد، مي بايست قيمت گذاري مورد نظر برگشت از محاسبه گيرد.   5- در صورتي كه در بازه تاريخ رسيد انبار، عمليات انبارگرداني تا مرحله  تائيد شمارش  انجام شده باشد، مي بايست برگشت از تائيد شمارش انجام گيرد.   6- در صورتي كه در بازه تاريخ رسيد انبار، براي تامين كننده صورتحساب صادر شده باشد، مي بايست حذف گردد.   7- در صورتي كه در بازه تاريخ رسيد انبار، فايل خريد و فروش فصلي و ماليات بر ارزش افزوده تهيه شده باشد مي بايست آنها را حذف كرد. ')), 0, 0, 349 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1899, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه مي توان مراحل توليد/مونتاژ محصول ABC با مصرف مواد اوليه A و B و C را در سپيدار بدون سيستم  سفارش كار  ثبت كرد؟    ')), Ltrim(RTrim('جهت ثبت توليد/مونتاژ محصول مي بايست مراحل ذيل را رعايت كرد:   1- ثبت رسيد انبار از نوع خريد (داخلي/وارداتي): با اين عمل اقلام خريداري شده A و B و C به انبار مواد اوليه اضافه ميگردد.   **نمونه سند در اين مرحله:   موجودي انبار مواد اوليه   xxx (بدهكار)       حسابهاي پرداختني          xxx (بستانكار)   2- ثبت خروج انبار از نوع مصرف: با اين عمل اقلام A و B و C مواد اوليه را به مركز هزينه (خط توليد) ارسال ميگردد.   **نمونه سند در اين مرحله:   كار در جريان ساخت          xxx (بدهكار)       موجودي انبار مواد اوليه       xxx (بستانكار)   3- ثبت رسيد انبار از نوع توليد: با اين عمل محصول توليد شده ABC از مركز هزينه(خط توليد) به انبار محصول اضافه ميگردد.   **نمونه سند در اين مرحله:   موجودي انبار محصولات   xxx (بدهكار)       كار در جريان ساخت           xxx (بستانكار)   ***توجه: در اين ثبت معين  كار در جريان ساخت  علاوه بر مبلغ مواد اوليه، مبلغ دستمزد و سربار را نيز شامل ميگردد كه اين امر باعث مانده دار شدن اين حساب با ماهيت بستانكار خواهد شد كه مي بايست با هزينه هاي سربار و دستمزد توليد تعديل گردد.   4- ثبت خروج انبار از نوع فروش: با اين عمل كه به همراه فاكتور فروش صادر مي شود، محصول ABC از انبار محصول خارج شده و به بهاي تمام شده كالاي فروش رفته سند خورده مي شود.   **نمونه سند در اين مرحله:   بهاي تمام شده كالاي فروش رفته   xxx (بدهكار)       موجودي انبار محصولات                    xxx (بستانكار)  توجه: * معين هاي مذكور مطابق با كدينگ پيش فرض سيستم است.* ')), 0, 0, 676 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1900, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('آيا مي توان سند حسابداري چندين رسيد انبار را به صورت تجميعي صادر كرد؟ چگونه؟ ')), Ltrim(RTrim('بله؛   جهت صدور سند حسابداري تجميعي به ترتيب ذيل عمل نمائيد:   1- در منوي عمودي وارد سيستم  تامين كنندگان و انبار  شده و از قسمت فهرست گزينه  رسيد انبار  را انتخاب نمائيد.   2- در صفحه باز شده با كليد هاي تركيبي Ctrl+M را انتخاب كرده يا از پايين صفحه گزينه  انتخاب چند گانه  را فعال كرده و رسيدهاي انبار را انتخاب نمائيد.   3- از بالاي صفحه، گزينه مربوط به  صدور سند حسابداري  را انتخاب نمائيد و پيغام  آيا مايل به صدور سند حسابداري به صورت جمعي هستيد  را با انتخاب گزينه  بله  تائيد نمائيد.  ***توجه: با اين عمليات به ازاي هر روز، يك سند حسابداري صادر خواهد شد. ')), 0, 0, 664 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1901, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان معين هاي پيش فرض رسيد انبار را تنظيم كرد؟ ')), Ltrim(RTrim('در منوي عمودي  تنظيمات  و از قسمت  عمليات ، گزينه تنظيمات را انتخاب كنيد، در صفحه باز شده سربرگ  تامين كنندگان و انبار  را انتخاب كرده سپس از قسمت معين هاي حسابداري انبار به ترتيب ذيل عمل نمائيد:   الف - جهت تعيين معين مربوط به رسيد انبار از نوع خريد (داخلي/وارداتي) گزينه هاي  حسابهاي پرداختني  و  واسط حمل  را ميتوان مشخص كرد.   ب - جهت تعيين معين مربوط به رسيد انبار از نوع خريد (توليد) گزينه  كار در جريان ساخت  را ميتوان مشخص كرد.   ج - جهت تعيين معين مربوط به رسيد انبار از نوع خريد (ساير) گزينه  ساير حسابها دريافتني  را ميتوان مشخص كرد.   د - جهت تعيين معين مربوط به رسيد انبار از نوع خريد (ساير - انبارگرداني) گزينه  اضافي انبار  را ميتوان مشخص كرد.   سپس پايين فرم كليد ذخيره را انتخاب كنيد. ')), 0, 0, 617 )
