Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1931, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('هنگام گزارش گيري از مرور حسابها، چرا گردش مالي مربوط به برخي عمليات ثبت شده در سيستم هاي (تامين كنندگان و انبار، دارايي ثابت، پيمانكاري و ...) نمايش داده نمي شود؟ ')), Ltrim(RTrim('دليل: عمليات ثبت شده در سيستم ها، داراي سند حسابداري نمي باشند.   راه حل اول:   الف - در منوي عمودي تك تك سيستم ها را انتخاب كرده و از قسمت فهرست گزينه عمليات مورد نظر را انتخاب نمائيد.   ب - در صفحه باز شده با كليد هاي تركيبي Ctrl+M را انتخاب كرده يا از پايين صفحه گزينه  انتخاب چند گانه  را فعال كرده و عمليات ثبت شده را انتخاب نمائيد.   3- از بالاي صفحه، گزينه مربوط به  صدور سند حسابداري  را انتخاب نمائيد.   4- در صورتي كه مايل به صور سند تجميعي باشيد، بعد از انتخاب گزينه صدور سند حسابداري، پيغام  آيا مايل به صدور سند حسابداري به صورت جمعي هستيد  را با انتخاب گزينه  بله  تائيد نمائيد.   ***توجه: با اين عمليات به ازاي هر روز، يك سند حسابداري صادر خواهد شد.   5- اما در صورتي كه مايل به صور سند تجميعي نمي باشيد بعد از انتخاب گزينه صدور سند حسابداري، پيغام  آيا مايل به صدور سند حسابداري به صورت جمعي هستيد  را با انتخاب گزينه  خير  تائيد نمائيد.   ***توجه: با اين عمليات به ازاي هر عمليات ثبت شده، يك سند حسابداري صادر خواهد شد. ')), 0, 0, 1676 )
