Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1946, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه مي توان از خالص مبالغ ثبت شده در اسناد  رسيد انبار  و  برگشت رسيد انبار  با نوع  خريد(داخلي)  گزارش تهيه نمود؟ ')), Ltrim(RTrim('از منوي تامين كنندگان و انبار، قسمت عمليات ، فرم مرور مبلغي انبار را باز كنيد. بازه زماني را از بالاي فرم انتخاب نموده و از تب اسناد، در ستون  نوع  كلمه  خريد  را فيلتر نموده و گزارش مورد نظر را تهيه نمايند.   نكته: در صورتي كه ستون مورد نظر از گزارش حذف شده باشد. با استفاده از آيكون انتخابگر ستون در پايين فرم گزينه  بازگرداندن تغييرات ستونها به حالت پيش فرض  را انتخاب نماييد تا وضعيت گزارش به حالت اوليه بازگردانده شود. و يا با انتخاب انتخابگر ستون ميتوان برخي ستونهاي مورد نظر را اضافه كرد. ')), 0, 0, 1327 )
