Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1955, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان از عمليات بدون  ماليات و عوارض ارزش افزوده  در سيستم(هاي) (تامين كنندگان و انبار/مشتريان و فروش) گزارش تهيه نمود؟ ')), Ltrim(RTrim('در منوي عمودي وارد سيستم(هاي) (تامين كنندگان و انبار/مشتريان و فروش) شده و در صورتي كه هدف كاربر تنها مشاهده اين عمليات باشد، مي بايست:   در منوي عمودي از قسمت عمليات گزينه  مرور(مبلغي انبار/مرور فروش)  را انتخاب كنيد.   بدون فيلتر كردن در هيچ يك از سربرگ ها، سربرگ  پيش فاكتور/فاكتور خريد/اسناد/اسناد فروش  را انتخاب كرده از ستون  ماليات و عوارض  گزينه  بزرگتر از  را فيلتر كرده و عدد   0  - صفر  را يادداشت كنيد.  ***توجه: در صورتي كه ستون هاي مذكور وجود نداشت، از پايين صفحه گزينه انتخابگر ستون را انتخاب كرده و از قسمت  انتخابگر ستون  در ليست باز شده مواردي كه لازم مي باشد را دوبار كليك كرده تا به ليست اضافه گردد. ')), 0, 0, 2616 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1956, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان از محتويات يك عمليات ثبت شده (خروج، رسيد و ...) در تامين كنندگان و انبار، گزارش تهيه نمود؟  ')), Ltrim(RTrim('در سيستم تامين كنندگان و انبار از قسمت  عمليات  گزينه مرور (تعدادي/مبلغي) انبار را انتخاب كرده و در صفحه باز شده سربرگ  اسناد  را انتخاب كرده و در صفحه باز شده به ترتيب ذيل عمل نمائيد: - با توجه به عمليات انجام شده در ستون  سند  كلمه  خروج، رسيد، برگشت خروج، برگشت رسيد  را يادداشت كرده و فيلتر كنيد. سپس متناسب سند انتخاب شده در ستون  نوع  كلمه  فروش، خريد(داخلي)، مصرف، ساير و ...  را يادداشت كرده و فيلتر كنيد. - در صورتي كه عمليات مورد نظر مشاهده گرديد، دابل كليك كرده و بعد از فيلتر شدن، سربرگ  اقلام  را انتخاب نمائيد.- در سربرگ انتخاب شده، تنها اقلام ثبت شده در عمليات نمايش داده خواهد شد.- در انتها مي توان از منوي زير، با استفاه از گزينه  منوي چاپ  مي توان از اطلاعات لازم خروجي پرينت تهيه نمود و  همچنين ميتوان خروجي اكسل نيز تهيه نمود.  ')), 0, 0, 2576 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1957, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان از توضيحات وارد شده در هر سطر از اقلام  رسيد انبار  گزارش تهيه نمود؟ ')), Ltrim(RTrim('در منوي سمت راست سيستم  تامين كنندگان و انبار  و در قسمت عمليات گزينه  مرور مبلغي/مرور تعدادي  را كليك نمائيد.   در صفحه باز شده سربرگ  اقلام  را انتخاب كرده و از ستون  توضيحات  موارد وارد شده را جستجو نمائيد.  **نكته1: درصورتي كه توضيحات كالاي خاصي مورد نظر مي باشد قبل از انتخاب ستون اقلام، ابتدا از سربرگ  كالا  كالاي مورد نظر را فيلتر نموده و سپس از سربرگ اقلام گزارش تهيه نمائيد.   **نكته2: در صورتي كه ستون مورد نظر قابل مشاهده نبود، جهت بازگرداندن موارد مخفي شده، مي بايست در فرم مورد نظر از پايين صفحه گزينه  انتخابگر ستون  را انتخاب كرده و سپس با انتخاب  بازگرداندن تغييرات ستونها به حالت پيش فرض  تغييرات پيش فرض را  بازيابي نمود   **نكته3: در صورتي كه لازم باشد تنها ستون  توضيحات  از ميان ستون ها فراخواني شود، پس از انتخاب گزينه انتخابگر ستون در ليست باز شده ستون مورد لازم را دوبار كليك كرده تا به ليست اضافه گردد. ')), 0, 0, 2918 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 1958, Ltrim(RTrim('Inventory/InventoryReceipt')), Ltrim(RTrim('رسيد انبار')), Ltrim(RTrim('چگونه ميتوان تنها دسترسي تعدادي انبار را براي كاربر فعال نمود؟ ')), Ltrim(RTrim('ابتدا مي بايست نرم افزار را به آخرين نسخه موجود بروز رساني نموده و به ترتيب ذيل عمل نمائيد:   1- با كاربر Admin وارد سپيدار شده سپس در منوي عمودي وارد سيستم  تامين كنندگان و انبار  شده و از قسمت عمليات گزينه  تنظيمات  را انتخاب نمائيد.   2- در فرم باز شده از سربرگ  تامين كنندگان و انبار  گزينه  اعمال دسترسي ريالي تعدادي انبار  را فعال كرده و بعد از ذخيره تنظيمات، از نرم افزار سپيدار خارج شده و مجدد با كاربر Admin سپيدار را اجرا نمائيد.   3- بعد از ورود مجدد به نرم افزار از منوي عمودي وارد سيستم  تنظيمات  شده و از قسمت فهرست گزينه  كاربران  را انتخاب نمائيد.   4- در صفحه باز شده كاربر مورد نظر را كليك راست كرده  ويرايش  را انتخاب نمائيد.   5- حال از سربرگ  حقوق و دسترسي  از قسمت  تامين كنندگان و انبار  گزينه  دسترسي ريالي انبار  را غير فعال نمائيد. ')), 0, 0, 2894 )
