Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 2124, Ltrim(RTrim('Inventory/InventoryReceiptReturn')), Ltrim(RTrim('برگشت رسيد انبار')), Ltrim(RTrim('چگونه ميتوان تنها دسترسي تعدادي انبار را براي كاربر فعال نمود؟ ')), Ltrim(RTrim('ابتدا مي بايست نرم افزار را به آخرين نسخه موجود بروز رساني نموده و به ترتيب ذيل عمل نمائيد:   1- با كاربر Admin وارد سپيدار شده سپس در منوي عمودي وارد سيستم  تامين كنندگان و انبار  شده و از قسمت عمليات گزينه  تنظيمات  را انتخاب نمائيد.   2- در فرم باز شده از سربرگ  تامين كنندگان و انبار  گزينه  اعمال دسترسي ريالي تعدادي انبار  را فعال كرده و بعد از ذخيره تنظيمات، از نرم افزار سپيدار خارج شده و مجدد با كاربر Admin سپيدار را اجرا نمائيد.   3- بعد از ورود مجدد به نرم افزار از منوي عمودي وارد سيستم  تنظيمات  شده و از قسمت فهرست گزينه  كاربران  را انتخاب نمائيد.   4- در صفحه باز شده كاربر مورد نظر را كليك راست كرده  ويرايش  را انتخاب نمائيد.   5- حال از سربرگ  حقوق و دسترسي  از قسمت  تامين كنندگان و انبار  گزينه  دسترسي ريالي انبار  را غير فعال نمائيد. ')), 0, 0, 2894 )
Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 2125, Ltrim(RTrim('Inventory/InventoryReceiptReturn')), Ltrim(RTrim('برگشت رسيد انبار')), Ltrim(RTrim('چگونه ميتوان از محتويات يك عمليات ثبت شده (خروج، رسيد و ...) در تامين كنندگان و انبار، گزارش تهيه نمود؟  ')), Ltrim(RTrim('در سيستم تامين كنندگان و انبار از قسمت  عمليات  گزينه مرور (تعدادي/مبلغي) انبار را انتخاب كرده و در صفحه باز شده سربرگ  اسناد  را انتخاب كرده و در صفحه باز شده به ترتيب ذيل عمل نمائيد:  با توجه به عمليات انجام شده در ستون  سند  كلمه  خروج، رسيد، برگشت خروج، برگشت رسيد  را يادداشت كرده و فيلتر كنيد. - سپس متناسب سند انتخاب شده در ستون  نوع  كلمه  فروش، خريد(داخلي)، مصرف، ساير و ...  را يادداشت كرده و فيلتر كنيد.- در صورتي كه عمليات مورد نظر مشاهده گرديد، دابل كليك كرده و بعد از فيلتر شدن، سربرگ  اقلام  را انتخاب نمائيد.- در سربرگ انتخاب شده، تنها اقلام ثبت شده در عمليات نمايش داده خواهد شد. - در انتها مي توان از منوي زير، با استفاه از گزينه  منوي چاپ  مي توان از اطلاعات لازم خروجي پرينت تهيه نمود و  همچنين ميتوان خروجي اكسل نيز تهيه نمود.  ')), 0, 0, 2576 )
