Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 2132, Ltrim(RTrim('Inventory/ProductPricing')), Ltrim(RTrim('قيمت گذاري محصولات توليدي')), Ltrim(RTrim('چگونه ميتوان رسيدهاي انبار از نوع توليد را بصورت يكجا قيمت گذاري نمود؟')), Ltrim(RTrim('براي قيمت گذاري رسيدهاي انبار از نوع توليد مراحل زير را طي ميكنيم :   1- از منوي عمليات سيستم تامين كنندگان و انبار، آيتم قيمت گذاري محصولات توليدي را انتخاب مي كنيم.   2- انبار مورد نظر را وارد كرده و محدوده تاريخي را نيز مشخص مي نماييم.   3- كليد نمايش اقلام را از پايين انتخاب كرده، ليست كالاها نمايش داده ميشود و سپس قيمت واحد را براي آن ها وارد مي كنيم.   4- سپس از بالاي صفحه بر روي آيكون آبي رنگ بروزرساني قيمت ها كليك مي كنيم.   5- پيغام روبرو ظاهر مي شود : براي برخي از اقلام موجود قبلا قيمت تعيين شده است. آيا مايل هستيد همه اقلام مجددا تعيين قيمت شوند؟   6- در صورت انتخاب گزينه بله تمامي كالاها با قيمت واحد وارد شده قيمت گذاري مي گردند و مبالغ از قبل وارد شده به قيمت جديد تغيير مي يابند.   7-در صورت انتخاب گزينه خير كالاهايي كه قبلا قيمت براي آنها مشخص گرديده تغيير پيدا نمي كنند و قيمت جديد فقط براي كالاهايي كه داراي قيمت نمي باشند اعمال ميگردد.')), 0, 0, 289 )
