Insert into fmk.FAQ (FAQID,[Key],Location,Question,Answer,ClubVersion,Version,ClubFAQRef) Values( 2255, Ltrim(RTrim('Inventory/ItemProperty')), Ltrim(RTrim('تعريف مشخصات كالا و خدمات')), Ltrim(RTrim('آيا ميتوان متناسب با ماهيت كالا، علاوه بر عنوان و ... مشخصه هاي ديگري براي (كالا/خدمت) مشخص نمود؟ ')), Ltrim(RTrim('بله؛   جهت تعريف مشخصه اي علاوه بر عنوان، كد، باركد و ... براي (كالا/خدمت) ، مي بايست شرايط ذيل رعايت گردد:   1- سيستم سپيدار را به نسخه 5.1.0 يا بالاتر بروز رساني نمائيد.   2-  در منوي عمودي وارد  تامين كنندگان و انبار  شده و از قسمت عمليات گزينه  تعريف مشخصات كالا/خدمت  را انتخاب نمائيد.   الف- در صورتي كه لازم باشد، مشخصه هنگام تعريف كالا/خدمت به صورت دستي وارد شود:   A- در صفحه باز شده، متناسب با مشخصات مورد نياز در تعريف كالا، عناوين مشخصه 1 تا 10 را نام گذاري نمائيد و از ستون  فعال ، گزينه فعال را براي آن مشخصه انتخاب كرده و فرم را ذخيره نمائيد.   B- با اين تنظيمات، ميتوان هنگام تعريف كالا/خدمت در تب  مشخصات  مشخصه مورد نظر را به صورت دستي تايپ نمائيد.   ب- در صورتي كه لازم باشد، مشخصه هنگام تعريف كالا/خدمت از قبل تعريف شده و تنها قابل انتخاب باشد:   A- در صفحه باز شده، متناسب با مشخصات مورد نياز در تعريف كالا، عناوين مشخصه 1 تا 10 را نام گذاري نمائيد و مقابل اين سطر، ستون هاي  فعال  و  قابل انتخاب  را فعال كرده و فرم را ذخيره نمائيد.   B- سپس از پايين صفحه با انتخاب گزينه (+) سطر ايجاد كرده و آيتم هاي آن مشخصه را تعريف نمائيد.   C- با اين تنظيمات، ميتوان هنگام تعريف كالا/خدمت در تب  مشخصات  مشخصه مورد نظر را انتخاب نمائيد. ')), 0, 0, 2842 )
